import React, { useState, useEffect } from 'react';
import '../Styles/Cart.css';
import CartItem from '../Components/CartItem';
import { 
  getCartItems,
  updateCartQuantity,
  removeFromCart,
  clearCart,
  checkout 
} from "../services/api";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch cart items
  const fetchCartData = async () => {
    try {
      console.log('üõí Fetching cart items...');
      setLoading(true);
      const response = await getCartItems();
      console.log('‚úÖ Raw cart data from API:', response.data);
      
      // Debug: Log first item details
      if (response.data.length > 0) {
        const firstItem = response.data[0];
        console.log('üîç First cart item details:', {
          id: firstItem.id,
          cartId: firstItem.cartId,
          cart_id: firstItem.cart_id,
          book_id: firstItem.book_id,
          quantity: firstItem.quantity,
          price: firstItem.price,
          typeOfPrice: typeof firstItem.price
        });
      }
      
      // Format items with proper data types
      const formattedItems = response.data.map(item => {
        // Parse price safely
        let price = 0;
        if (item.price) {
          if (typeof item.price === 'number') {
            price = item.price;
          } else if (typeof item.price === 'string') {
            price = parseFloat(item.price);
            if (isNaN(price)) price = 0;
          }
        }
        
        // Parse quantity - default to 1 if invalid
        let quantity = parseInt(item.quantity);
        if (isNaN(quantity) || quantity < 1) quantity = 1;
        if (quantity > 10) quantity = 10;
        
        return {
          ...item,
          id: item.id || item.cart_id || item.cartId,
          cartId: item.id || item.cart_id || item.cartId,
          cart_id: item.cart_id || item.id || item.cartId,
          bookId: item.book_id,
          price: price,
          quantity: quantity,
          available_quantity: parseInt(item.available_quantity || item.book_quantity || 10)
        };
      });
      
      console.log('‚úÖ Final formatted cart items:', formattedItems);
      setCartItems(formattedItems);
      setError(null);
    } catch (err) {
      console.error('‚ùå Error fetching cart items:', err);
      setError(`Failed to load cart: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCartData();
  }, []);

  // Update quantity
  const handleUpdateQuantity = async (cartItemId, newQuantity) => {
    console.log(`üîÑ handleUpdateQuantity: cartItemId=${cartItemId}, newQuantity=${newQuantity}`);
    
    // Validate input
    if (!cartItemId || cartItemId === 'undefined') {
      setError('Invalid cart item ID');
      return;
    }
    
    if (newQuantity < 1 || newQuantity > 10) {
      setError('Quantity must be between 1 and 10');
      return;
    }
    
    // Find the item to check available stock
    const item = cartItems.find(item => item.id == cartItemId);
    if (item) {
      const availableQty = item.available_quantity || 10;
      if (newQuantity > availableQty) {
        setError(`Only ${availableQty} available in stock`);
        return;
      }
    }
    
    try {
      const response = await updateCartQuantity(cartItemId, newQuantity);
      console.log('‚úÖ Update response:', response.data);
      
      if (response.data.success) {
        // Update local state
        setCartItems(prevItems => 
          prevItems.map(item => 
            item.id == cartItemId ? { ...item, quantity: newQuantity } : item
          )
        );
        setSuccessMessage('Quantity updated!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setError(response.data.error || 'Update failed');
      }
    } catch (err) {
      console.error('‚ùå Error updating quantity:', err);
      console.error('Error details:', err.response?.data);
      
      if (err.response?.status === 404) {
        setError('Cart item not found. It may have been removed.');
        // Refresh cart to get current state
        setTimeout(() => fetchCartData(), 1000);
      } else {
        setError(err.response?.data?.error || 'Failed to update quantity');
      }
    }
  };

  // Remove item
  const handleRemoveItem = async (cartItemId) => {
    try {
      const response = await removeFromCart(cartItemId);
      
      if (response.data.success) {
        setCartItems(prevItems => 
          prevItems.filter(item => item.id != cartItemId)
        );
        setSuccessMessage('Item removed!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 3000);
      }
    } catch (err) {
      console.error('Error removing item:', err);
      setError(err.response?.data?.error || 'Failed to remove item');
    }
  };

  // Clear cart
  const handleClearCart = async () => {
    try {
      await clearCart();
      setCartItems([]);
      setSuccessMessage('Cart cleared!');
      setError(null);
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      console.error('Error clearing cart:', err);
      setError('Failed to clear cart');
    }
  };

  // Checkout
  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      setError('Cart is empty');
      return;
    }

    try {
      const response = await checkout();
      
      if (response.data.success) {
        const summaryText = response.data.orderSummary?.map(item => 
          `‚Ä¢ ${item.title} (x${item.quantity}): $${item.subtotal}`
        ).join('\n') || 'No summary available';
        
        alert(`‚úÖ ORDER PLACED!\n\nOrder ID: ${response.data.orderId}\nTotal: $${response.data.total}\n\n${summaryText}\n\nThank you!`);
        
        setCartItems([]);
        setSuccessMessage('Order placed successfully!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 5000);
      }
    } catch (err) {
      console.error('Checkout error:', err);
      
      if (err.response?.data?.outOfStock) {
        const outOfStockList = err.response.data.outOfStock.map(item => 
          `‚Ä¢ ${item.title}: Only ${item.available} available (requested ${item.requested})`
        ).join('\n');
        
        alert(`‚ùå CHECKOUT FAILED!\n\nOut of stock:\n${outOfStockList}`);
        setError('Some items are out of stock');
        fetchCartData(); // Refresh cart
      } else {
        alert(`‚ùå Checkout failed: ${err.response?.data?.error || err.message}`);
        setError(err.response?.data?.error || 'Checkout failed');
      }
    }
  };

  // Calculate order summary
  const calculateOrderSummary = () => {
    const subtotal = cartItems.reduce((sum, item) => {
      const price = item.price || 0;
      const quantity = item.quantity || 1;
      return sum + (price * quantity);
    }, 0);
    
    const tax = subtotal * 0.10;
    const shipping = subtotal > 0 ? 0 : 0;
    const total = subtotal + tax + shipping;
    
    return {
      subtotal: subtotal.toFixed(2),
      tax: tax.toFixed(2),
      shipping: shipping.toFixed(2),
      total: total.toFixed(2),
      shippingText: 'FREE'
    };
  };

  const orderSummary = calculateOrderSummary();

  if (loading) {
    return (
      <div className="cart-container">
        <h1>OnlineBookStore - Shopping Cart</h1>
        <div className="loading">
          <div className="spinner"></div>
          Loading cart...
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>OnlineBookStore - Shopping Cart</h1>
      
      {/* Debug info */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{
          background: 'rgba(255,255,255,0.1)',
          padding: '10px',
          borderRadius: '8px',
          marginBottom: '15px',
          color: 'white'
        }}>
          <small>Debug: {cartItems.length} items | 
          <button onClick={() => console.log('Cart items:', cartItems)} style={{marginLeft: '10px'}}>
            Log Cart
          </button>
          <button onClick={fetchCartData} style={{marginLeft: '10px'}}>
            Refresh
          </button></small>
        </div>
      )}
      
      {/* Messages */}
      {successMessage && (
        <div className="success-message">
          ‚úÖ {successMessage}
        </div>
      )}
      
      {error && (
        <div className="error-message">
          ‚ùå {error}
        </div>
      )}
      
      <div className="cart-content">
        <div className="cart-items-section">
          <div style={{
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center', 
            marginBottom: '20px'
          }}>
            <h2>
              Your Cart ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'})
            </h2>
            {cartItems.length > 0 && (
              <button 
                onClick={handleClearCart}
                style={{
                  background: '#ff4757',
                  color: 'white',
                  border: 'none',
                  padding: '8px 16px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '0.9rem'
                }}
              >
                Clear All
              </button>
            )}
          </div>
          
          {cartItems.length === 0 ? (
            <div className="empty-cart">
              <p style={{fontSize: '1.2rem', marginBottom: '20px', color: '#666'}}>
                Your shopping cart is empty
              </p>
              <a href="/home" className="continue-shopping-btn">
                ‚Üê Continue Shopping
              </a>
            </div>
          ) : (
            <div className="cart-items-list">
              {cartItems.map((item) => (
                <CartItem
                  key={item.id}
                  item={item}
                  onUpdateQuantity={handleUpdateQuantity}
                  onRemoveItem={handleRemoveItem}
                />
              ))}
            </div>
          )}
        </div>
        
        {cartItems.length > 0 && (
          <div className="order-summary">
            <h3>Order Summary</h3>
            <div className="summary-details">
              <div className="summary-row">
                <span>Subtotal ({cartItems.length} items):</span>
                <span style={{fontWeight: '600'}}>${orderSummary.subtotal}</span>
              </div>
              <div className="summary-row">
                <span>Shipping:</span>
                <span style={{color: '#28a745', fontWeight: '600'}}>{orderSummary.shippingText}</span>
              </div>
              <div className="summary-row">
                <span>Tax (10%):</span>
                <span>${orderSummary.tax}</span>
              </div>
              <div className="summary-row total">
                <span>Total Amount:</span>
                <span style={{fontSize: '1.4rem', fontWeight: '700', color: '#2d3748'}}>
                  ${orderSummary.total}
                </span>
              </div>
            </div>
            
            <button className="checkout-btn" onClick={handleCheckout}>
              Proceed to Checkout
            </button>
            
            <div className="cart-actions">
              <a href="/home" className="continue-link">‚Üê Continue Shopping</a>
              <a href="/library" className="back-link">Browse More Books ‚Üí</a>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
