// Cart.js - Updated version
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '..Styles';
import CartItem from './CartItem';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch cart items
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        console.log('Fetching cart items...');
        const response = await axios.get('http://localhost:5000/api/cart');
        console.log('Fetched cart items:', response.data);
        setCartItems(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching cart items:', err);
        setError('Failed to load cart items');
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  // Update quantity function
  const handleUpdateQuantity = async (itemId, newQuantity) => {
    if (!itemId || itemId === 'undefined') {
      console.error('Cannot update quantity: itemId is undefined or invalid');
      return;
    }

    console.log(`Updating cart item ${itemId} to quantity ${newQuantity}`);
    
    try {
      const response = await axios.put(
        `http://localhost:5000/api/cart/${itemId}`,
        { quantity: newQuantity }
      );
      
      console.log('Update response:', response.data);
      
      if (response.data.success) {
        // Update local state
        setCartItems(prevItems => 
          prevItems.map(item => 
            item.id === itemId ? { ...item, quantity: newQuantity } : item
          )
        );
      } else {
        console.error('Update failed:', response.data.message);
        setError(`Failed to update quantity: ${response.data.message}`);
      }
    } catch (err) {
      console.error('Error updating quantity:', err);
      setError('Failed to update quantity. Please try again.');
    }
  };

  // Remove item function
  const handleRemoveItem = async (itemId) => {
    if (!itemId) {
      console.error('Cannot remove item: itemId is undefined');
      return;
    }

    try {
      const response = await axios.delete(`http://localhost:5000/api/cart/${itemId}`);
      
      if (response.data.success) {
        setCartItems(prevItems => prevItems.filter(item => item.id !== itemId));
      }
    } catch (err) {
      console.error('Error removing item:', err);
      setError('Failed to remove item');
    }
  };

  // Calculate order summary
  const calculateOrderSummary = () => {
    const subtotal = cartItems.reduce((sum, item) => {
      const price = parseFloat(item.price) || 0;
      const quantity = item.quantity || 0;
      return sum + (price * quantity);
    }, 0);
    
    const tax = subtotal * 0.10; // 10% tax
    const total = subtotal + tax;
    
    return {
      subtotal: subtotal.toFixed(2),
      tax: tax.toFixed(2),
      total: total.toFixed(2),
      shipping: subtotal > 0 ? 'FREE' : '0.00'
    };
  };

  const orderSummary = calculateOrderSummary();

  if (loading) {
    return <div className="loading">Loading cart...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="cart-container">
      <h1>OnlineBookStore</h1>
      
      <div className="cart-content">
        <div className="cart-items-section">
          <h2>Cart Items ({cartItems.length})</h2>
          
          {cartItems.length === 0 ? (
            <div className="empty-cart">
              <p>Your cart is empty</p>
              <a href="/" className="continue-shopping-btn">
                Continue Shopping
              </a>
            </div>
          ) : (
            <div className="cart-items-list">
              {cartItems.map((item) => (
                <CartItem
                  key={item.id} // IMPORTANT: Added unique key
                  item={item}
                  onUpdateQuantity={handleUpdateQuantity}
                  onRemoveItem={handleRemoveItem}
                />
              ))}
            </div>
          )}
        </div>
        
        {cartItems.length > 0 && (
          <div className="order-summary">
            <h3>Order Summary</h3>
            <div className="summary-details">
              <div className="summary-row">
                <span>Subtotal ({cartItems.length} items):</span>
                <span>${orderSummary.subtotal}</span>
              </div>
              <div className="summary-row">
                <span>Shipping:</span>
                <span>{orderSummary.shipping}</span>
              </div>
              <div className="summary-row">
                <span>Tax (10%):</span>
                <span>${orderSummary.tax}</span>
              </div>
              <div className="summary-row total">
                <span>Total:</span>
                <span>${orderSummary.total}</span>
              </div>
            </div>
            
            <button className="checkout-btn">
              Proceed to Checkout
            </button>
            
            <div className="cart-actions">
              <a href="/" className="continue-link">Continue Shopping</a>
              <a href="/" className="back-link">Back to Home</a>
            </div>
            
            <div className="guarantee">
              <p>Secure payment – 100% satisfaction guarantee – Easy returns</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
