// CartItem.js - Fixed version
import React, { useState } from 'react';
import '../Styles/ca';

const CartItem = ({ item, onUpdateQuantity, onRemoveItem }) => {
  const [quantity, setQuantity] = useState(item.quantity || 1);
  const [error, setError] = useState('');

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity < 1 || newQuantity > 10) {
      setError('Quantity must be between 1 and 10');
      return;
    }
    
    setQuantity(newQuantity);
    setError('');
    
    // Use the cart item ID (id from cart table)
    if (onUpdateQuantity && item.id) {
      onUpdateQuantity(item.id, newQuantity);
    } else {
      console.error('Cannot update: item.id is undefined', item);
      setError('Failed to update quantity: item not found');
    }
  };

  const handleIncrement = () => {
    handleQuantityChange(quantity + 1);
  };

  const handleDecrement = () => {
    if (quantity > 1) {
      handleQuantityChange(quantity - 1);
    }
  };

  const handleRemove = () => {
    if (onRemoveItem && item.id) {
      onRemoveItem(item.id); // Use cart item ID for removal
    }
  };

  return (
    <div className="cart-item">
      <div className="item-image">
        <img 
          src={item.image_url || '/default-book-cover.jpg'} 
          alt={item.title} 
        />
      </div>
      
      <div className="item-details">
        <h3 className="item-title">{item.title}</h3>
        <p className="item-author">by {item.author}</p>
        <p className="item-category">{item.genre || 'Fiction'}</p>
        
        <div className="item-rating">
          <span className="rating-stars">{"★".repeat(Math.floor(item.rating || 4))}</span>
          <span className="rating-value">{item.rating || 4.0}</span>
        </div>
        
        <p className="item-pages">{item.pages || 300} pages - {item.published_year || 2023}</p>
        
        {error && <p className="error-message">{error}</p>}
      </div>
      
      <div className="item-quantity">
        <button 
          onClick={handleDecrement}
          className="quantity-btn"
          aria-label="Decrease quantity"
          disabled={quantity <= 1}
        >
          −
        </button>
        
        <input
          type="number"
          min="1"
          max="10"
          value={quantity}
          onChange={(e) => handleQuantityChange(parseInt(e.target.value) || 1)}
          className="quantity-input"
        />
        
        <button 
          onClick={handleIncrement}
          className="quantity-btn"
          aria-label="Increase quantity"
          disabled={quantity >= 10}
        >
          +
        </button>
      </div>
      
      <div className="item-price">
        <p className="price">${((item.price || 0) * quantity).toFixed(2)}</p>
        <p className="unit-price">${(item.price || 0).toFixed(2)} each</p>
        <button 
          onClick={handleRemove}
          className="remove-btn"
          aria-label="Remove item"
        >
          Remove
        </button>
      </div>
    </div>
  );
};

export default CartItem;
