import React, { useState, useEffect, useMemo } from 'react';
import '../Styles/Library.css';
import Navbar from '../Components/NavBar';
import { 
  getBooks, 
  searchBooks, 
  filterBooksByGenre,
  addToCart as addToCartAPI,
  getCartItems
} from '../services/api';

const Library = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('title');
  const [filterGenre, setFilterGenre] = useState('all');
  const [hoveredBook, setHoveredBook] = useState(null);
  const [genres, setGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cartCount, setCartCount] = useState(0);
  const [searchResults, setSearchResults] = useState([]);
  const [searchLoading, setSearchLoading] = useState(false);

  // Fetch books from database on initial load
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);
        const response = await getBooks();
        setBooks(response.data);
        setSearchResults(response.data); // Initialize search results with all books
        
        // Extract unique genres from database
        const uniqueGenres = [...new Set(response.data.map(book => book.genre))];
        setGenres(['all', ...uniqueGenres]);
        
        console.log(`Loaded ${response.data.length} books from database`);
      } catch (error) {
        console.error('Error fetching books:', error);
        alert('‚ùå Failed to load books from database. Please check your backend connection.');
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  // Load cart count from database
  useEffect(() => {
    const fetchCartCount = async () => {
      try {
        const response = await getCartItems();
        setCartCount(response.data.length);
      } catch (error) {
        console.error('Error fetching cart count:', error);
      }
    };

    fetchCartCount();
  }, []);

  // Search books in database
  useEffect(() => {
    const searchBooksFromDB = async () => {
      if (searchTerm.trim() === '') {
        // If search is empty, show all books
        try {
          const response = await getBooks();
          setSearchResults(response.data);
        } catch (error) {
          console.error('Error fetching books:', error);
        }
        return;
      }

      try {
        setSearchLoading(true);
        const response = await searchBooks(searchTerm);
        setSearchResults(response.data);
      } catch (error) {
        console.error('Error searching books:', error);
        alert('‚ùå Error searching books. Please try again.');
      } finally {
        setSearchLoading(false);
      }
    };

    const delayDebounce = setTimeout(() => {
      searchBooksFromDB();
    }, 500);

    return () => clearTimeout(delayDebounce);
  }, [searchTerm]);

  // Filter books by genre from database
  useEffect(() => {
    const filterBooksFromDB = async () => {
      if (filterGenre === 'all') {
        try {
          const response = await getBooks();
          setSearchResults(response.data);
        } catch (error) {
          console.error('Error fetching all books:', error);
        }
        return;
      }

      try {
        setLoading(true);
        const response = await filterBooksByGenre(filterGenre);
        setSearchResults(response.data);
      } catch (error) {
        console.error('Error filtering books:', error);
        alert('‚ùå Error filtering books. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    filterBooksFromDB();
  }, [filterGenre]);

  // Sort books locally (client-side)
  const filteredAndSortedBooks = useMemo(() => {
    return [...searchResults].sort((a, b) => {
      switch (sortOption) {
        case 'title':
          return a.title.localeCompare(b.title);
        case 'author':
          return a.author.localeCompare(b.author);
        case 'price-low':
          return parseFloat(a.price) - parseFloat(b.price);
        case 'price-high':
          return parseFloat(b.price) - parseFloat(a.price);
        case 'rating':
          return parseFloat(b.rating) - parseFloat(a.rating);
        case 'year':
          return parseInt(b.published_year) - parseInt(a.published_year);
        default:
          return 0;
      }
    });
  }, [searchResults, sortOption]);

  // Add to cart function - DATABASE DRIVEN
  const addToCart = async (book) => {
    try {
      console.log('Adding to cart from database:', book);
      
      // Call API to add to database
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.action === 'added' || response.data.action === 'updated') {
        // Update local cart count
        const cartResponse = await getCartItems();
        setCartCount(cartResponse.data.length);
        
        // Show success message
        alert(`‚úÖ Added "${book.title}" to cart!`);
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('‚ùå Error adding book to cart. Please try again.');
    }
  };

  // Quick checkout function - DATABASE DRIVEN (doesn't auto-checkout)
  const quickCheckout = async (book) => {
    try {
      console.log('Quick checkout for:', book);
      
      // Clear existing cart first using API
      const clearResponse = await fetch(`http://localhost:5000/api/cart/clear/1`, {
        method: 'DELETE'
      });
      
      if (!clearResponse.ok) {
        throw new Error('Failed to clear cart');
      }
      
      // Add the single book to cart
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.action === 'added') {
        // Set quick checkout flag (but DON'T auto-checkout)
        localStorage.setItem('isQuickCheckout', 'true');
        
        // Update cart count
        const cartResponse = await getCartItems();
        setCartCount(cartResponse.data.length);
        
        // Navigate to cart page for REVIEW
        alert(`‚úÖ "${book.title}" added for quick checkout!\n\nReview your order on the cart page.`);
        window.location.href = '/cart';
      }
    } catch (error) {
      console.error('Error in quick checkout:', error);
      alert('‚ùå Error during quick checkout. Please try again.');
    }
  };

  // Reset all filters
  const resetFilters = () => {
    setSearchTerm('');
    setFilterGenre('all');
    setSortOption('title');
  };

  // Get unique authors for information
  const uniqueAuthors = useMemo(() => {
    return [...new Set(books.map(book => book.author))].length;
  }, [books]);

  if (loading && books.length === 0) {
    return (
      <>
        <Navbar cartCount={cartCount} />
        <div className="library-container">
          <div className="animated-background"></div>
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '50vh',
            color: 'white',
            fontSize: '1.5rem',
            flexDirection: 'column',
            gap: '20px'
          }}>
            <div className="spinner"></div>
            Loading books from database...
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar cartCount={cartCount} />
      <div className="library-container">
        <div className="animated-background"></div>
        
        {/* Library Header */}
        <div className="library-header">
          <h1 className="library-title">Digital Library</h1>
          <p className="library-subtitle">
            Browse our collection of {books.length} books by {uniqueAuthors} authors
          </p>
        </div>

        {/* Controls Section */}
        <div className="controls-section">
          <div className="search-container">
            <input
              type="text"
              placeholder="üîç Search books, authors, or genres..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchLoading && (
              <div className="search-loading">
                <div className="mini-spinner"></div>
              </div>
            )}
          </div>

          <div className="filter-sort-container">
            <select 
              value={filterGenre} 
              onChange={(e) => setFilterGenre(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Genres</option>
              {genres.filter(genre => genre !== 'all').map(genre => (
                <option key={genre} value={genre}>
                  {genre}
                </option>
              ))}
            </select>

            <select 
              value={sortOption} 
              onChange={(e) => setSortOption(e.target.value)}
              className="sort-select"
            >
              <option value="title">Sort by Title</option>
              <option value="author">Sort by Author</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Sort by Rating</option>
              <option value="year">Sort by Year</option>
            </select>

            {(searchTerm || filterGenre !== 'all' || sortOption !== 'title') && (
              <button 
                onClick={resetFilters}
                className="reset-filters-btn"
                style={{
                  background: 'transparent',
                  border: '2px solid rgba(74, 222, 128, 0.6)',
                  color: '#e2e8f0',
                  padding: '15px 25px',
                  borderRadius: '25px',
                  cursor: 'pointer',
                  fontWeight: '600',
                  transition: 'all 0.3s ease'
                }}
              >
                Reset Filters
              </button>
            )}
          </div>
        </div>

        {/* Results Count */}
        <div className="results-count">
          {searchLoading ? (
            'Searching books...'
          ) : (
            <>
              Showing {filteredAndSortedBooks.length} of {books.length} books
              {searchTerm && ` for "${searchTerm}"`}
              {filterGenre !== 'all' && ` in ${filterGenre}`}
              {sortOption !== 'title' && ` ‚Ä¢ Sorted by ${sortOption.replace('-', ' ')}`}
            </>
          )}
        </div>

        {/* Floating Cart Button with count */}
        <div className="floating-cart-btn">
          <button 
            className="cart-icon-btn"
            onClick={() => window.location.href = '/cart'}
          >
            üõí
            {cartCount > 0 && (
              <span className="cart-badge">{cartCount}</span>
            )}
          </button>
        </div>

        {/* Main Content - Books Grid */}
        <div className="library-content">
          <div className="books-grid-container">
            {filteredAndSortedBooks.length === 0 ? (
              <div className="no-books-found">
                <h3>üìö No books found</h3>
                <p>Try a different search term or filter</p>
                <button 
                  onClick={resetFilters}
                  className="show-all-btn"
                >
                  Show All Books
                </button>
              </div>
            ) : (
              <div className="books-grid">
                {filteredAndSortedBooks.map(book => (
                  <div 
                    key={book.id} 
                    className="book-card"
                    onMouseEnter={() => setHoveredBook(book)}
                    onMouseLeave={() => setHoveredBook(null)}
                  >
                    <div className="book-image-container">
                      <img 
                        src={book.image_url || `https://picsum.photos/300/400?random=${book.id}`} 
                        alt={book.title} 
                        className="book-image" 
                        onError={(e) => {
                          e.target.src = `https://picsum.photos/300/400?random=${book.id}`;
                        }}
                      />
                      <div className="book-overlay">
                        <button 
                          className="add-to-cart-btn"
                          onClick={() => addToCart(book)}
                        >
                          üõí Add to Cart
                        </button>
                        <button 
                          className="quick-checkout-btn"
                          onClick={() => quickCheckout(book)}
                        >
                          ‚ö° Quick Checkout
                        </button>
                      </div>
                    </div>
                    
                    <div className="book-info">
                      <h3 className="book-title">{book.title}</h3>
                      <p className="book-author">by {book.author}</p>
                      <div className="book-meta">
                        <span className="book-genre">{book.genre}</span>
                        <span className="book-rating">‚≠ê {book.rating || '4.0'}</span>
                      </div>
                      <div className="book-details">
                        <span className="book-year">{book.published_year || '2023'}</span>
                        <span className="book-pages">{book.pages || '300'} pages</span>
                      </div>
                      <p className="book-price">${parseFloat(book.price).toFixed(2)}</p>
                    </div>

                    {/* Hover Description */}
                    {hoveredBook?.id === book.id && (
                      <div className="book-description">
                        <h4>About this book:</h4>
                        <p>{book.description || 'A captivating book that will keep you engaged from start to finish.'}</p>
                        <div className="description-actions">
                          <button 
                            className="description-add-cart"
                            onClick={() => addToCart(book)}
                          >
                            Add to Cart
                          </button>
                          <button 
                            className="description-checkout"
                            onClick={() => quickCheckout(book)}
                          >
                            Quick Checkout
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Add CSS for loading spinners */}
      <style jsx>{`
        .spinner {
          width: 50px;
          height: 50px;
          border: 5px solid rgba(79, 70, 229, 0.3);
          border-radius: 50%;
          border-top-color: #4f46e5;
          animation: spin 1s ease-in-out infinite;
        }
        
        .mini-spinner {
          width: 20px;
          height: 20px;
          border: 2px solid rgba(79, 70, 229, 0.3);
          border-radius: 50%;
          border-top-color: #4f46e5;
          animation: spin 1s ease-in-out infinite;
          position: absolute;
          right: 25px;
          top: 50%;
          transform: translateY(-50%);
        }
        
        .search-loading {
          position: absolute;
          right: 20px;
          top: 50%;
          transform: translateY(-50%);
        }
        
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
        
        .no-books-found {
          text-align: center;
          color: white;
          padding: 50px;
          width: 100%;
          background: rgba(30, 41, 59, 0.8);
          border-radius: 20px;
          border: 1px solid rgba(74, 222, 128, 0.3);
        }
        
        .no-books-found h3 {
          font-size: 1.8rem;
          margin-bottom: 15px;
          color: #e2e8f0;
        }
        
        .no-books-found p {
          color: #94a3b8;
          margin-bottom: 25px;
          font-size: 1.1rem;
        }
        
        .show-all-btn {
          background: linear-gradient(45deg, #4f46e5, #7c3aed);
          color: white;
          border: none;
          padding: 12px 30px;
          border-radius: 25px;
          cursor: pointer;
          font-weight: 600;
          font-size: 1rem;
          transition: all 0.3s ease;
        }
        
        .show-all-btn:hover {
          transform: translateY(-2px);
          box-shadow: 0 5px 15px rgba(79, 70, 229, 0.4);
        }
        
        .reset-filters-btn:hover {
          background: rgba(74, 222, 128, 0.1);
        }
      `}</style>
    </>
  );
};

export default Library;
