// Cart.js - Fixed and Updated Version
import React, { useState, useEffect } from 'react';
import '../Styles/Cart.css';
import CartItem from '../Components/CartItem';
import { 
  getCartItems,
  updateCartQuantity,
  removeFromCart,
  clearCart 
} from "../services/api";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch cart items
  useEffect(() => {
    const fetchCartItemsData = async () => {
      try {
        console.log('Fetching cart items...');
        const response = await getCartItems();
        console.log('Fetched cart items:', response.data);
        
        // Debug: Log item structure
        console.log('Cart items structure:');
        response.data.forEach((item, index) => {
          console.log(`Item ${index + 1}:`, {
            id: item.id,
            cart_id: item.cart_id,
            book_id: item.book_id,
            title: item.title,
            quantity: item.quantity
          });
        });
        
        // Ensure each item has proper structure
        const formattedItems = response.data.map(item => ({
          ...item,
          id: item.id || item.cart_id, // Use id or cart_id
          cart_id: item.cart_id || item.id, // Ensure cart_id exists
          bookId: item.book_id, // Include bookId for reference
          price: parseFloat(item.price) || 0,
          quantity: parseInt(item.quantity) || 1
        }));
        
        console.log('Formatted items:', formattedItems);
        setCartItems(formattedItems);
        setLoading(false);
        setError(null);
      } catch (err) {
        console.error('Error fetching cart items:', err);
        setError('Failed to load cart items. Please check if the backend is running.');
        setLoading(false);
      }
    };

    fetchCartItemsData();
  }, []);

  // Update quantity function
  const handleUpdateQuantity = async (cartItemId, newQuantity) => {
    console.log('handleUpdateQuantity called with:', { cartItemId, newQuantity });
    
    if (!cartItemId) {
      console.error('Cannot update quantity: cartItemId is undefined');
      setError('Cannot update quantity: Invalid cart item');
      return;
    }

    try {
      console.log(`Updating cart item ${cartItemId} to quantity ${newQuantity}`);
      const response = await updateCartQuantity(cartItemId, newQuantity);
      
      console.log('Update response:', response.data);
      
      if (response.data.success) {
        // Update local state
        setCartItems(prevItems => 
          prevItems.map(item => 
            (item.id === cartItemId || item.cart_id === cartItemId) 
              ? { ...item, quantity: newQuantity } 
              : item
          )
        );
        setSuccessMessage('Quantity updated successfully!');
        setError(null);
        
        // Clear success message after 3 seconds
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        console.error('Update failed:', response.data.error);
        setError(`Failed to update quantity: ${response.data.error || 'Unknown error'}`);
      }
    } catch (err) {
      console.error('Error updating quantity:', err);
      console.error('Error details:', err.response?.data || err.message);
      setError(`Failed to update quantity: ${err.response?.data?.error || err.message || 'Please try again.'}`);
    }
  };

  // Remove item function
  const handleRemoveItem = async (cartItemId) => {
    console.log('handleRemoveItem called with:', cartItemId);
    
    if (!cartItemId) {
      console.error('Cannot remove item: cartItemId is undefined');
      setError('Cannot remove item: Invalid cart item');
      return;
    }

    try {
      const response = await removeFromCart(cartItemId);
      console.log('Remove response:', response.data);
      
      if (response.data.success) {
        // Remove from local state
        setCartItems(prevItems => 
          prevItems.filter(item => 
            !(item.id === cartItemId || item.cart_id === cartItemId)
          )
        );
        setSuccessMessage('Item removed from cart!');
        setError(null);
        
        // Clear success message after 3 seconds
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setError('Failed to remove item');
      }
    } catch (err) {
      console.error('Error removing item:', err);
      setError(`Failed to remove item: ${err.response?.data?.error || err.message || 'Please try again.'}`);
    }
  };

  // Clear cart function
  const handleClearCart = async () => {
    try {
      const response = await clearCart();
      if (response.data.success) {
        setCartItems([]);
        setSuccessMessage('Cart cleared successfully!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 3000);
      }
    } catch (err) {
      console.error('Error clearing cart:', err);
      setError('Failed to clear cart');
    }
  };

  // Calculate order summary
  const calculateOrderSummary = () => {
    const subtotal = cartItems.reduce((sum, item) => {
      const price = parseFloat(item.price) || 0;
      const quantity = parseInt(item.quantity) || 1;
      return sum + (price * quantity);
    }, 0);
    
    const tax = subtotal * 0.10; // 10% tax
    const shipping = subtotal > 0 ? 0 : 0; // Free shipping
    const total = subtotal + tax + shipping;
    
    return {
      subtotal: subtotal.toFixed(2),
      tax: tax.toFixed(2),
      shipping: shipping.toFixed(2),
      total: total.toFixed(2),
      shippingText: subtotal > 0 ? 'FREE' : '$0.00'
    };
  };

  const orderSummary = calculateOrderSummary();

  if (loading) {
    return (
      <div className="cart-container">
        <h1>OnlineBookStore - Shopping Cart</h1>
        <div className="loading">
          <div className="spinner"></div>
          Loading your cart...
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>OnlineBookStore - Shopping Cart</h1>
      
      {/* Success and Error Messages */}
      {successMessage && (
        <div className="success-message" style={{
          background: '#d4edda',
          color: '#155724',
          padding: '12px 20px',
          borderRadius: '6px',
          marginBottom: '20px',
          borderLeft: '4px solid #28a745',
          display: 'flex',
          alignItems: 'center',
          gap: '10px'
        }}>
          ‚úÖ {successMessage}
        </div>
      )}
      
      {error && (
        <div className="error-message" style={{
          background: '#f8d7da',
          color: '#721c24',
          padding: '12px 20px',
          borderRadius: '6px',
          marginBottom: '20px',
          borderLeft: '4px solid #dc3545',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            ‚ùå {error}
          </div>
          <button 
            onClick={() => window.location.reload()}
            style={{
              background: '#dc3545',
              color: 'white',
              border: 'none',
              padding: '5px 15px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '0.9rem',
              whiteSpace: 'nowrap'
            }}
          >
            Retry
          </button>
        </div>
      )}
      
      <div className="cart-content">
        <div className="cart-items-section">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <h2>Your Cart ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'})</h2>
            {cartItems.length > 0 && (
              <button 
                onClick={handleClearCart}
                style={{
                  background: '#ff4757',
                  color: 'white',
                  border: 'none',
                  padding: '8px 16px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '500'
                }}
              >
                Clear Cart
              </button>
            )}
          </div>
          
          {cartItems.length === 0 ? (
            <div className="empty-cart">
              <p style={{ fontSize: '1.2rem', marginBottom: '20px', color: '#666' }}>Your cart is empty</p>
              <a href="/home" className="continue-shopping-btn">
                Continue Shopping
              </a>
            </div>
          ) : (
            <div className="cart-items-list">
              {cartItems.map((item) => (
                <CartItem
                  key={item.id || item.cart_id} // Use cart item ID as key
                  item={item}
                  onUpdateQuantity={handleUpdateQuantity}
                  onRemoveItem={handleRemoveItem}
                />
              ))}
            </div>
          )}
        </div>
        
        {cartItems.length > 0 && (
          <div className="order-summary">
            <h3>Order Summary</h3>
            <div className="summary-details">
              <div className="summary-row">
                <span>Subtotal ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'}):</span>
                <span style={{ fontWeight: '600' }}>${orderSummary.subtotal}</span>
              </div>
              <div className="summary-row">
                <span>Shipping:</span>
                <span style={{ color: '#28a745', fontWeight: '600' }}>{orderSummary.shippingText}</span>
              </div>
              <div className="summary-row">
                <span>Tax (10%):</span>
                <span>${orderSummary.tax}</span>
              </div>
              <div className="summary-row total">
                <span>Total Amount:</span>
                <span style={{ fontSize: '1.4rem', fontWeight: '700', color: '#2d3748' }}>
                  ${orderSummary.total}
                </span>
              </div>
            </div>
            
            <button 
              className="checkout-btn"
              onClick={() => {
                alert(`Order placed successfully!\nTotal: $${orderSummary.total}\nThank you for your purchase!`);
                // Clear cart after checkout
                handleClearCart();
              }}
            >
              Proceed to Checkout
            </button>
            
            <div className="cart-actions">
              <a href="/home" className="continue-link">‚Üê Continue Shopping</a>
              <a href="/library" className="back-link">Browse More Books ‚Üí</a>
            </div>
            
            <div className="guarantee">
              <p style={{ fontSize: '0.9rem', color: '#666', lineHeight: '1.5' }}>
                üîí Secure payment ‚Ä¢ ‚úÖ 100% satisfaction guarantee ‚Ä¢ ‚Ü©Ô∏è Easy returns within 30 days ‚Ä¢ üöö Free shipping on all orders
              </p>
            </div>
          </div>
        )}
      </div>
      
      {/* Debug Info (remove in production) */}
      {process.env.NODE_ENV === 'development' && cartItems.length > 0 && (
        <div style={{
          marginTop: '30px',
          padding: '15px',
          background: '#f8f9fa',
          borderRadius: '8px',
          fontSize: '0.85rem',
          color: '#666',
          border: '1px solid #dee2e6'
        }}>
          <strong>Debug Info:</strong> Showing {cartItems.length} cart items. 
          First item ID: {cartItems[0]?.id}, Cart ID: {cartItems[0]?.cart_id}
        </div>
      )}
    </div>
  );
};

export default Cart;
