import React, { useState, useMemo } from 'react';
import './style/Library.css';

const Library = () => {
  // Sample book data
  const initialBooks = [
    {
      id: 1,
      title: "The Great Gatsby",
      author: "F. Scott Fitzgerald",
      price: 12.99,
      image: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400",
      description: "A classic novel of the Jazz Age, exploring themes of idealism, resistance to change, and excess.",
      genre: "Classic",
      rating: 4.5
    },
    {
      id: 2,
      title: "To Kill a Mockingbird",
      author: "Harper Lee",
      price: 14.99,
      image: "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400",
      description: "A gripping tale of racial injustice and childhood innocence in the American South.",
      genre: "Fiction",
      rating: 4.8
    },
    {
      id: 3,
      title: "1984",
      author: "George Orwell",
      price: 11.99,
      image: "https://images.unsplash.com/photo-1532012197267-da84d127e765?w=400",
      description: "A dystopian social science fiction novel about totalitarian control and surveillance.",
      genre: "Science Fiction",
      rating: 4.7
    },
    {
      id: 4,
      title: "Pride and Prejudice",
      author: "Jane Austen",
      price: 10.99,
      image: "https://images.unsplash.com/photo-1544716278-ca5e3f4abd8c?w=400",
      description: "A romantic novel that critiques the British landed gentry at the end of the 18th century.",
      genre: "Romance",
      rating: 4.6
    },
    {
      id: 5,
      title: "The Hobbit",
      author: "J.R.R. Tolkien",
      price: 16.99,
      image: "https://images.unsplash.com/photo-1621351183012-e2f9972dd9bf?w=400",
      description: "A fantasy novel about Bilbo Baggins' unexpected journey to reclaim treasure.",
      genre: "Fantasy",
      rating: 4.9
    },
    {
      id: 6,
      title: "Harry Potter",
      author: "J.K. Rowling",
      price: 18.99,
      image: "https://images.unsplash.com/photo-1621351183012-e2f9972dd9bf?w=400",
      description: "The story of a young wizard's magical adventures at Hogwarts School.",
      genre: "Fantasy",
      rating: 4.9
    }
  ];

  const [books, setBooks] = useState(initialBooks);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('title');
  const [filterGenre, setFilterGenre] = useState('all');
  const [cart, setCart] = useState([]);
  const [hoveredBook, setHoveredBook] = useState(null);

  // Get unique genres for filter
  const genres = ['all', ...new Set(initialBooks.map(book => book.genre))];

  // Filter and sort books
  const filteredAndSortedBooks = useMemo(() => {
    let filtered = books.filter(book => 
      book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      book.author.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (filterGenre !== 'all') {
      filtered = filtered.filter(book => book.genre === filterGenre);
    }

    return filtered.sort((a, b) => {
      switch (sortOption) {
        case 'title':
          return a.title.localeCompare(b.title);
        case 'author':
          return a.author.localeCompare(b.author);
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'rating':
          return b.rating - a.rating;
        default:
          return 0;
      }
    });
  }, [books, searchTerm, sortOption, filterGenre]);

  const addToCart = (book) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === book.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === book.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...book, quantity: 1 }];
    });
  };

  const removeFromCart = (bookId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== bookId));
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const proceedToCheckout = () => {
    alert(`Proceeding to checkout with ${cart.length} items. Total: $${getTotalPrice().toFixed(2)}`);
    // Here you would typically redirect to a checkout page
  };

  return (
    <div className="library-container">
      {/* Header */}
      <header className="library-header">
        <h1 className="library-title">üìö Book Haven</h1>
        <p className="library-subtitle">Discover Your Next Favorite Read</p>
      </header>

      {/* Controls Section */}
      <div className="controls-section">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search books or authors..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <span className="search-icon">üîç</span>
        </div>

        <div className="filter-sort-container">
          <select 
            value={filterGenre} 
            onChange={(e) => setFilterGenre(e.target.value)}
            className="filter-select"
          >
            {genres.map(genre => (
              <option key={genre} value={genre}>
                {genre.charAt(0).toUpperCase() + genre.slice(1)}
              </option>
            ))}
          </select>

          <select 
            value={sortOption} 
            onChange={(e) => setSortOption(e.target.value)}
            className="sort-select"
          >
            <option value="title">Sort by Title</option>
            <option value="author">Sort by Author</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="rating">Sort by Rating</option>
          </select>
        </div>
      </div>

      {/* Main Content */}
      <div className="library-content">
        {/* Books Grid */}
        <div className="books-grid">
          {filteredAndSortedBooks.map(book => (
            <div 
              key={book.id} 
              className="book-card"
              onMouseEnter={() => setHoveredBook(book)}
              onMouseLeave={() => setHoveredBook(null)}
            >
              <div className="book-image-container">
                <img src={book.image} alt={book.title} className="book-image" />
                <div className="book-overlay">
                  <button 
                    className="add-to-cart-btn"
                    onClick={() => addToCart(book)}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
              
              <div className="book-info">
                <h3 className="book-title">{book.title}</h3>
                <p className="book-author">by {book.author}</p>
                <div className="book-meta">
                  <span className="book-genre">{book.genre}</span>
                  <span className="book-rating">‚≠ê {book.rating}</span>
                </div>
                <p className="book-price">${book.price}</p>
              </div>

              {/* Hover Description */}
              {hoveredBook?.id === book.id && (
                <div className="book-description">
                  <p>{book.description}</p>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Cart Sidebar */}
        <div className="cart-sidebar">
          <h3>Shopping Cart ({cart.length})</h3>
          {cart.length === 0 ? (
            <p className="empty-cart">Your cart is empty</p>
          ) : (
            <>
              <div className="cart-items">
                {cart.map(item => (
                  <div key={item.id} className="cart-item">
                    <span className="cart-item-title">{item.title}</span>
                    <span className="cart-item-quantity">x{item.quantity}</span>
                    <span className="cart-item-price">${(item.price * item.quantity).toFixed(2)}</span>
                    <button 
                      className="remove-item-btn"
                      onClick={() => removeFromCart(item.id)}
                    >
                      √ó
                    </button>
                  </div>
                ))}
              </div>
              <div className="cart-total">
                <strong>Total: ${getTotalPrice().toFixed(2)}</strong>
              </div>
              <button 
                className="checkout-btn"
                onClick={proceedToCheckout}
              >
                Proceed to Checkout
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Library;
