// Cart.js - Fixed version
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../Styles/Cart.css';
import CartItem from '../CartItem';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch cart items
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        console.log('Fetching cart items...');
        const response = await axios.get('http://localhost:5000/api/cart');
        console.log('Fetched cart items:', response.data);
        
        // Ensure each item has proper structure
        const formattedItems = response.data.map(item => ({
          ...item,
          id: item.id, // This is the cart item ID
          cart_id: item.id, // Also include as cart_id
          bookId: item.book_id, // Include bookId for reference
          price: parseFloat(item.price) || 0,
          quantity: parseInt(item.quantity) || 1
        }));
        
        setCartItems(formattedItems);
        setLoading(false);
        setError(null);
      } catch (err) {
        console.error('Error fetching cart items:', err);
        setError('Failed to load cart items. Please check if the backend is running.');
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  // Update quantity function
  const handleUpdateQuantity = async (cartItemId, newQuantity) => {
    if (!cartItemId) {
      console.error('Cannot update quantity: cartItemId is undefined');
      setError('Cannot update quantity: Invalid cart item');
      return;
    }

    console.log(`Updating cart item ${cartItemId} to quantity ${newQuantity}`);
    
    try {
      const response = await axios.put(
        `http://localhost:5000/api/cart/${cartItemId}`,
        { quantity: newQuantity }
      );
      
      console.log('Update response:', response.data);
      
      if (response.data.success) {
        // Update local state
        setCartItems(prevItems => 
          prevItems.map(item => 
            item.id === cartItemId ? { ...item, quantity: newQuantity } : item
          )
        );
        setSuccessMessage('Quantity updated successfully!');
        setError(null);
        
        // Clear success message after 3 seconds
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        console.error('Update failed:', response.data.error);
        setError(`Failed to update quantity: ${response.data.error}`);
      }
    } catch (err) {
      console.error('Error updating quantity:', err);
      setError('Failed to update quantity. Please try again.');
    }
  };

  // Remove item function
  const handleRemoveItem = async (cartItemId) => {
    if (!cartItemId) {
      console.error('Cannot remove item: cartItemId is undefined');
      setError('Cannot remove item: Invalid cart item');
      return;
    }

    try {
      const response = await axios.delete(`http://localhost:5000/api/cart/${cartItemId}`);
      
      if (response.data.success) {
        // Remove from local state
        setCartItems(prevItems => prevItems.filter(item => item.id !== cartItemId));
        setSuccessMessage('Item removed from cart!');
        setError(null);
        
        // Clear success message after 3 seconds
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setError('Failed to remove item');
      }
    } catch (err) {
      console.error('Error removing item:', err);
      setError('Failed to remove item. Please try again.');
    }
  };

  // Calculate order summary
  const calculateOrderSummary = () => {
    const subtotal = cartItems.reduce((sum, item) => {
      const price = parseFloat(item.price) || 0;
      const quantity = parseInt(item.quantity) || 1;
      return sum + (price * quantity);
    }, 0);
    
    const tax = subtotal * 0.10; // 10% tax
    const shipping = subtotal > 0 ? 0 : 0; // Free shipping
    const total = subtotal + tax + shipping;
    
    return {
      subtotal: subtotal.toFixed(2),
      tax: tax.toFixed(2),
      shipping: shipping.toFixed(2),
      total: total.toFixed(2),
      shippingText: subtotal > 0 ? 'FREE' : '$0.00'
    };
  };

  const orderSummary = calculateOrderSummary();

  if (loading) {
    return (
      <div className="cart-container">
        <h1>OnlineBookStore - Shopping Cart</h1>
        <div className="loading">Loading your cart...</div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>OnlineBookStore - Shopping Cart</h1>
      
      {/* Success and Error Messages */}
      {successMessage && (
        <div className="success-message" style={{
          background: '#d4edda',
          color: '#155724',
          padding: '12px 20px',
          borderRadius: '6px',
          marginBottom: '20px',
          borderLeft: '4px solid #28a745'
        }}>
          ‚úÖ {successMessage}
        </div>
      )}
      
      {error && (
        <div className="error-message" style={{
          background: '#f8d7da',
          color: '#721c24',
          padding: '12px 20px',
          borderRadius: '6px',
          marginBottom: '20px',
          borderLeft: '4px solid #dc3545'
        }}>
          ‚ùå {error}
          <button 
            onClick={() => window.location.reload()}
            style={{
              marginLeft: '15px',
              background: '#dc3545',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Retry
          </button>
        </div>
      )}
      
      <div className="cart-content">
        <div className="cart-items-section">
          <h2>Your Cart ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'})</h2>
          
          {cartItems.length === 0 ? (
            <div className="empty-cart">
              <p style={{ fontSize: '1.2rem', marginBottom: '20px' }}>Your cart is empty</p>
              <a href="/home" className="continue-shopping-btn">
                Continue Shopping
              </a>
            </div>
          ) : (
            <div className="cart-items-list">
              {cartItems.map((item) => (
                <CartItem
                  key={item.id} // Use cart item ID as key
                  item={item}
                  onUpdateQuantity={handleUpdateQuantity}
                  onRemoveItem={handleRemoveItem}
                />
              ))}
            </div>
          )}
        </div>
        
        {cartItems.length > 0 && (
          <div className="order-summary">
            <h3>Order Summary</h3>
            <div className="summary-details">
              <div className="summary-row">
                <span>Subtotal ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'}):</span>
                <span style={{ fontWeight: '600' }}>${orderSummary.subtotal}</span>
              </div>
              <div className="summary-row">
                <span>Shipping:</span>
                <span style={{ color: '#28a745', fontWeight: '600' }}>{orderSummary.shippingText}</span>
              </div>
              <div className="summary-row">
                <span>Tax (10%):</span>
                <span>${orderSummary.tax}</span>
              </div>
              <div className="summary-row total">
                <span>Total Amount:</span>
                <span style={{ fontSize: '1.4rem', fontWeight: '700', color: '#2d3748' }}>
                  ${orderSummary.total}
                </span>
              </div>
            </div>
            
            <button 
              className="checkout-btn"
              onClick={() => {
                alert(`Order placed successfully!\nTotal: $${orderSummary.total}\nThank you for your purchase!`);
                // Clear cart after checkout
                axios.delete('http://localhost:5000/api/cart/clear/1')
                  .then(() => {
                    setCartItems([]);
                    setSuccessMessage('Order placed successfully! Cart cleared.');
                  })
                  .catch(err => console.error('Error clearing cart:', err));
              }}
            >
              Proceed to Checkout
            </button>
            
            <div className="cart-actions">
              <a href="/home" className="continue-link">‚Üê Continue Shopping</a>
              <a href="/library" className="back-link">Browse More Books ‚Üí</a>
            </div>
            
            <div className="guarantee">
              <p style={{ fontSize: '0.9rem', color: '#666', lineHeight: '1.5' }}>
                üîí Secure payment ‚Ä¢ ‚úÖ 100% satisfaction guarantee ‚Ä¢ ‚Ü©Ô∏è Easy returns within 30 days ‚Ä¢ üöö Free shipping on all orders
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
