import React, { useState, useEffect } from "react";
import Navbar from "../Components/NavBar";
import '../Styles/Home.css';
import { 
  getBooks,
  addToCart as addToCartAPI,
  getCartItems
} from "../services/api";

export default function HomePage() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cartCount, setCartCount] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);
        const response = await getBooks();
        setBooks(response.data);
      } catch (error) {
        console.error('Error fetching books:', error);
        setErrorMessage('Failed to load books');
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  useEffect(() => {
    const fetchCartCount = async () => {
      try {
        const response = await getCartItems();
        setCartCount(response.data.length);
      } catch (error) {
        console.error('Error fetching cart count:', error);
      }
    };

    fetchCartCount();
  }, []);

  const addToCart = async (book) => {
    try {
      if (!book.id) {
        alert('‚ùå Book ID is missing');
        return;
      }
      
      if (book.quantity <= 0) {
        alert('‚ùå This book is out of stock');
        return;
      }
      
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.success) {
        const cartResponse = await getCartItems();
        setCartCount(cartResponse.data.length);
        alert(`‚úÖ Added "${book.title}" to cart!\n\nAvailable stock: ${response.data.available}`);
        setErrorMessage('');
      } else {
        alert(`‚ùå ${response.data.message || response.data.error || 'Failed to add to cart'}`);
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert(`‚ùå ${error.response?.data?.error || 'Error adding to cart'}`);
    }
  };

  const quickCheckout = async (book) => {
    try {
      if (!book.id) {
        alert('‚ùå Book ID is missing');
        return;
      }
      
      if (book.quantity <= 0) {
        alert('‚ùå This book is out of stock');
        return;
      }
      
      // Clear existing cart first
      await fetch('http://localhost:5000/api/cart/clear/1', { method: 'DELETE' });
      
      // Add the single book to cart
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.success) {
        const cartResponse = await getCartItems();
        setCartCount(cartResponse.data.length);
        window.location.href = '/cart';
      }
    } catch (error) {
      console.error('Error in quick checkout:', error);
      alert('‚ùå Error during quick checkout');
    }
  };

  // Get popular categories
  const popularCategories = ['Science Fiction', 'Fantasy', 'Mystery', 'Business'];

  if (loading && books.length === 0) {
    return (
      <>
        <Navbar cartCount={cartCount} />
        <div className="home-container">
          <div className="hero-section">
            <div className="hero-content">
              <h1>Welcome to the Online Book Store</h1>
              <p>Loading books from database...</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar cartCount={cartCount} />
      <div className="home-container">
        <div className="hero-section">
          <div className="hero-content">
            <h1>Welcome to the Online Book Store</h1>
            <p>Discover your next favorite book from our collection of {books.length} books</p>
          </div>
        </div>

        <div className="categories-section">
          <div className="container">
            <h2 className="section-title">Popular Categories</h2>
            
            {popularCategories.map(category => {
              const booksInCategory = books.filter(book => book.genre === category).slice(0, 5);
              
              return (
                <div key={category} className="category-row">
                  <h3 className="category-title">{category}</h3>
                  <div className="books-row">
                    {booksInCategory.map(book => (
                      <div key={book.id} className="book-card">
                        <div className="book-image-container">
                          <img 
                            src={book.image_url || `https://picsum.photos/300/400?random=${book.id}`} 
                            alt={book.title} 
                            className="book-image"
                          />
                          <div className="book-overlay">
                            <button 
                              className="add-to-cart-btn"
                              onClick={() => addToCart(book)}
                              disabled={book.quantity <= 0}
                              style={book.quantity <= 0 ? {opacity: 0.5, cursor: 'not-allowed'} : {}}
                            >
                              {book.quantity <= 0 ? 'Out of Stock' : 'üõí Add to Cart'}
                            </button>
                            <button 
                              className="quick-checkout-btn"
                              onClick={() => quickCheckout(book)}
                              disabled={book.quantity <= 0}
                              style={book.quantity <= 0 ? {opacity: 0.5, cursor: 'not-allowed'} : {}}
                            >
                              ‚ö° Quick Buy
                            </button>
                          </div>
                        </div>
                        
                        <div className="book-info">
                          <h4 className="book-title">{book.title}</h4>
                          <p className="book-author">by {book.author}</p>
                          <div className="book-meta">
                            <span className="book-rating">‚≠ê {book.rating || '4.0'}</span>
                            <span className="book-stock" style={{
                              color: book.quantity > 5 ? '#28a745' : book.quantity > 0 ? '#ffc107' : '#dc3545',
                              fontSize: '0.8rem'
                            }}>
                              {book.quantity > 5 ? '‚úì In Stock' : book.quantity > 0 ? `‚ö† ${book.quantity} left` : '‚úó Out'}
                            </span>
                          </div>
                          <p className="book-price">${parseFloat(book.price || 0).toFixed(2)}</p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        <div className="cta-section">
          <div className="container">
            <h2>Ready to Explore More?</h2>
            <p>Discover our complete collection with {books.length} books across all genres</p>
            <button 
              className="browse-all-btn"
              onClick={() => window.location.href = '/library'}
            >
              Browse Full Library
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
