import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000,
});

// Get current user ID from localStorage
const getCurrentUserId = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  return user ? user.id : null;
};

// ========== BOOK APIs ==========
export const getBooks = () => API.get('/books');
export const searchBooks = (term) => API.get(`/books/search?term=${encodeURIComponent(term)}`);
export const filterBooksByGenre = (genre) => API.get(`/books/filter/${encodeURIComponent(genre)}`);

// ========== CART APIs ==========
export const addToCart = (bookId) => {
  const userId = getCurrentUserId();
  if (!userId) {
    return Promise.reject(new Error('User not logged in'));
  }
  return API.post('/cart/add', { userId, bookId });
};

export const getCartItems = () => {
  const userId = getCurrentUserId();
  if (!userId) {
    return Promise.resolve({ data: [] });
  }
  return API.get(`/cart?userId=${userId}`);
};

export const removeFromCart = (cartItemId) => {
  const userId = getCurrentUserId();
  if (!userId) {
    return Promise.reject(new Error('User not logged in'));
  }
  return API.delete(`/cart/${cartItemId}`, { data: { userId } });
};

export const clearCart = () => {
  const userId = getCurrentUserId();
  if (!userId) {
    return Promise.reject(new Error('User not logged in'));
  }
  // NOTE: Changed endpoint from /cart/clear to /cart/clear/:userId
  return API.delete(`/cart/clear/${userId}`);
};

// ========== USER APIs ==========
export const loginUser = (username, password) => {
  return API.post('/login', { username, password });
};

export const registerUser = (username, password) => {
  return API.post('/register', { username, password });
};

// Add error interceptor
API.interceptors.response.use(
  (response) => {
    console.log('✅ API Response:', response.config.url, response.status);
    return response;
  },
  (error) => {
    console.error('❌ API Error:', error.config?.url, error.response?.status, error.message);
    return Promise.reject(error);
  }
);

export default API;
