import React, { useState } from 'react';
import '../Styles/CartItem.css';

const CartItem = ({ item, onUpdateQuantity, onRemoveItem }) => {
  const [quantity, setQuantity] = useState(item.quantity || 1);

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity < 1 || newQuantity > 10) return;
    if (newQuantity > item.available_quantity) {
      alert(`Only ${item.available_quantity} available in stock`);
      return;
    }
    
    setQuantity(newQuantity);
    onUpdateQuantity(item.id, newQuantity);
  };

  const handleIncrement = () => {
    handleQuantityChange(quantity + 1);
  };

  const handleDecrement = () => {
    if (quantity > 1) {
      handleQuantityChange(quantity - 1);
    }
  };

  const handleRemove = () => {
    if (window.confirm(`Remove "${item.title}" from cart?`)) {
      onRemoveItem(item.id);
    }
  };

  return (
    <div className="cart-item">
      <div className="item-image">
        <img 
          src={item.image_url || 'https://picsum.photos/120/160'} 
          alt={item.title} 
        />
      </div>
      
      <div className="item-details">
        <h3 className="item-title">{item.title}</h3>
        <p className="item-author">by {item.author}</p>
        <p className="item-category">{item.genre}</p>
        
        <div className="item-stock">
          {item.available_quantity > 5 ? (
            <span style={{color: '#28a745'}}>✓ In Stock ({item.available_quantity})</span>
          ) : item.available_quantity > 0 ? (
            <span style={{color: '#ffc107'}}>⚠ Low Stock ({item.available_quantity})</span>
          ) : (
            <span style={{color: '#dc3545'}}>✗ Out of Stock</span>
          )}
        </div>
      </div>
      
      <div className="item-quantity">
        <button onClick={handleDecrement} className="quantity-btn" disabled={quantity <= 1}>
          −
        </button>
        
        <input
          type="number"
          min="1"
          max={Math.min(10, item.available_quantity)}
          value={quantity}
          onChange={(e) => handleQuantityChange(parseInt(e.target.value) || 1)}
          className="quantity-input"
        />
        
        <button onClick={handleIncrement} className="quantity-btn" disabled={quantity >= 10 || quantity >= item.available_quantity}>
          +
        </button>
      </div>
      
      <div className="item-price">
        <p className="price">${((item.price || 0) * quantity).toFixed(2)}</p>
        <p className="unit-price">${(item.price || 0).toFixed(2)} each</p>
        <button onClick={handleRemove} className="remove-btn">
          Remove
        </button>
      </div>
    </div>
  );
};

export default CartItem;
