import React, { useState, useEffect } from 'react';
import Navbar from '../Components/NavBar';
import '../Styles/Cart.css';
import { 
  getCartItems, 
  removeFromCart, 
  updateCartQuantity,
  clearCart,
  getCartItemCount
} from '../services/api';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isQuickCheckout, setIsQuickCheckout] = useState(false);
  const [cartCount, setCartCount] = useState(0);
  const [checkoutProcessing, setCheckoutProcessing] = useState(false);
  const [updatingItemId, setUpdatingItemId] = useState(null); // Track which item is being updated

  // Load cart items from database
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        setLoading(true);
        const response = await getCartItems();
        console.log('Fetched cart items:', response.data);
        setCartItems(response.data);
        setCartCount(response.data.length);
        
        // Check if it's a quick checkout
        const quickCheckoutFlag = localStorage.getItem('isQuickCheckout');
        if (quickCheckoutFlag) {
          setIsQuickCheckout(true);
        }
      } catch (error) {
        console.error('Error fetching cart items:', error);
        alert('‚ùå Failed to load cart items. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  // Update quantity in database - FIXED VERSION
  const updateQuantity = async (cartId, newQuantity) => {
    if (newQuantity < 1) {
      // If quantity becomes 0 or negative, remove item instead
      removeFromCartHandler(cartId);
      return;
    }
    
    try {
      setUpdatingItemId(cartId); // Mark this item as being updated
      console.log(`Updating cart item ${cartId} to quantity ${newQuantity}`);
      
      // Update in database
      const response = await updateCartQuantity(cartId, newQuantity);
      console.log('Update response:', response.data);
      
      if (response.data.affectedRows > 0) {
        // Update local state - CORRECTED: Only update the specific item
        setCartItems(prevItems =>
          prevItems.map(item => {
            if (item.cart_id === cartId) {
              console.log(`Updating item ${cartId} from ${item.quantity} to ${newQuantity}`);
              return { ...item, quantity: newQuantity };
            }
            return item; // Keep other items unchanged
          })
        );
        
        // Update cart count (calculate total items)
        updateCartCount();
      } else {
        alert('‚ùå Failed to update quantity. Item not found.');
      }
    } catch (error) {
      console.error('Error updating quantity:', error);
      alert('‚ùå Error updating quantity. Please try again.');
    } finally {
      setUpdatingItemId(null); // Clear updating flag
    }
  };

  // Remove item from cart in database
  const removeFromCartHandler = async (cartId) => {
    try {
      await removeFromCart(cartId);
      
      // Update local state - remove only this item
      setCartItems(prevItems => prevItems.filter(item => item.cart_id !== cartId));
      
      // Update cart count
      updateCartCount();
      
      alert('‚úÖ Item removed from cart');
      
      // If it's quick checkout and cart becomes empty, remove the flag
      if (isQuickCheckout && cartItems.length <= 1) {
        setIsQuickCheckout(false);
        localStorage.removeItem('isQuickCheckout');
      }
    } catch (error) {
      console.error('Error removing from cart:', error);
      alert('‚ùå Error removing item. Please try again.');
    }
  };

  // Update cart count
  const updateCartCount = async () => {
    try {
      const response = await getCartItems();
      setCartCount(response.data.length);
    } catch (error) {
      console.error('Error updating cart count:', error);
    }
  };

  // Calculate subtotal
  const calculateSubtotal = () => {
    return cartItems.reduce((total, item) => 
      total + (parseFloat(item.price) * item.quantity), 0
    ).toFixed(2);
  };

  // Calculate tax (10%)
  const calculateTax = () => {
    return (parseFloat(calculateSubtotal()) * 0.1).toFixed(2);
  };

  // Calculate total
  const calculateTotal = () => {
    const subtotal = parseFloat(calculateSubtotal());
    const tax = parseFloat(calculateTax());
    return (subtotal + tax).toFixed(2);
  };

  // Calculate total items count
  const calculateTotalItems = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  // Handle checkout
  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      alert('üõí Your cart is empty!');
      return;
    }

    try {
      setCheckoutProcessing(true);
      
      // Simulate payment processing
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Create order summary
      const orderSummary = {
        orderId: 'ORD-' + Date.now(),
        items: cartItems.map(item => ({
          title: item.title,
          quantity: item.quantity,
          price: item.price,
          total: (parseFloat(item.price) * item.quantity).toFixed(2)
        })),
        subtotal: calculateSubtotal(),
        tax: calculateTax(),
        total: calculateTotal(),
        timestamp: new Date().toISOString()
      };
      
      // Show order confirmation
      alert(`‚úÖ Order Confirmed!\n\nOrder ID: ${orderSummary.orderId}\nTotal: $${orderSummary.total}\n\nThank you for your purchase!`);
      
      // Clear cart after successful checkout
      await clearCart();
      setCartItems([]);
      setCartCount(0);
      
      // Clear quick checkout flag if it exists
      setIsQuickCheckout(false);
      localStorage.removeItem('isQuickCheckout');
      
    } catch (error) {
      console.error('Error during checkout:', error);
      alert('‚ùå Error processing checkout. Please try again.');
    } finally {
      setCheckoutProcessing(false);
    }
  };

  // Clear entire cart
  const handleClearCart = async () => {
    if (cartItems.length === 0) return;
    
    if (window.confirm('Are you sure you want to clear your entire cart?')) {
      try {
        await clearCart();
        setCartItems([]);
        setCartCount(0);
        
        // Clear quick checkout flag
        setIsQuickCheckout(false);
        localStorage.removeItem('isQuickCheckout');
        
        alert('‚úÖ Cart cleared successfully');
      } catch (error) {
        console.error('Error clearing cart:', error);
        alert('‚ùå Error clearing cart. Please try again.');
      }
    }
  };

  // Convert to regular cart (remove quick checkout flag)
  const convertToRegularCart = () => {
    setIsQuickCheckout(false);
    localStorage.removeItem('isQuickCheckout');
    alert('‚úÖ Switched to regular checkout mode');
  };

  if (loading) {
    return (
      <>
        <Navbar cartCount={cartCount} />
        <div className="cart-container">
          <div className="cart-empty">
            <h2>Loading Cart...</h2>
            <p>Fetching your items from the database</p>
          </div>
        </div>
      </>
    );
  }

  if (cartItems.length === 0) {
    return (
      <>
        <Navbar cartCount={cartCount} />
        <div className="cart-container">
          <div className="cart-empty">
            <h2>Your Cart is Empty</h2>
            <p>Add some books to get started!</p>
            <button 
              className="browse-books-btn"
              onClick={() => window.location.href = '/library'}
            >
              Browse Books
            </button>
            <button 
              className="browse-books-btn"
              onClick={() => window.location.href = '/home'}
              style={{ marginTop: '10px', background: '#667eea' }}
            >
              Go to Home
            </button>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar cartCount={cartCount} />
      <div className="cart-container">
        <div className="cart-header">
          <h1>Shopping Cart</h1>
          {isQuickCheckout ? (
            <>
              <div className="quick-checkout-banner">
                ‚ö° Quick Checkout - Review your single item below
                <button 
                  onClick={convertToRegularCart}
                  style={{
                    marginLeft: '15px',
                    background: 'transparent',
                    border: '1px solid white',
                    color: 'white',
                    padding: '5px 10px',
                    borderRadius: '15px',
                    fontSize: '0.8rem',
                    cursor: 'pointer'
                  }}
                >
                  Switch to Regular Checkout
                </button>
              </div>
              <p className="cart-summary-text">
                You're checking out 1 item quickly. Review and confirm your order.
              </p>
            </>
          ) : (
            <p className="cart-summary-text">
              You have {calculateTotalItems()} item{calculateTotalItems() !== 1 ? 's' : ''} in your cart
            </p>
          )}
        </div>

        <div className="cart-content">
          <div className="cart-items-section">
            <div className="cart-items-header">
              <h3>Cart Items ({cartItems.length})</h3>
              <button 
                className="clear-cart-btn"
                onClick={handleClearCart}
                style={{
                  background: 'transparent',
                  color: '#e74c3c',
                  border: '1px solid #e74c3c',
                  padding: '8px 16px',
                  borderRadius: '20px',
                  cursor: 'pointer',
                  fontSize: '0.9rem'
                }}
              >
                Clear All
              </button>
            </div>
            
            <div className="cart-items">
              {cartItems.map(item => (
                <div key={item.cart_id} className="cart-item">
                  <div className="item-image">
                    <img 
                      src={item.image_url || `https://picsum.photos/300/400?random=${item.id}`} 
                      alt={item.title}
                      onError={(e) => {
                        e.target.src = `https://picsum.photos/300/400?random=${item.id}`;
                      }}
                    />
                  </div>
                  
                  <div className="item-details">
                    <h3 className="item-title">{item.title}</h3>
                    <p className="item-author">by {item.author}</p>
                    <p className="item-genre">{item.genre}</p>
                    <div className="item-rating">‚≠ê {item.rating || '4.0'}</div>
                    <p className="item-pages">{item.pages || '300'} pages ‚Ä¢ {item.published_year || '2023'}</p>
                  </div>

                  <div className="item-controls">
                    <div className="quantity-controls">
                      <button 
                        onClick={() => updateQuantity(item.cart_id, item.quantity - 1)}
                        disabled={item.quantity <= 1 || updatingItemId === item.cart_id}
                      >
                        {updatingItemId === item.cart_id ? '...' : '-'}
                      </button>
                      <span>
                        {updatingItemId === item.cart_id ? (
                          <span className="updating-spinner"></span>
                        ) : (
                          item.quantity
                        )}
                      </span>
                      <button 
                        onClick={() => updateQuantity(item.cart_id, item.quantity + 1)}
                        disabled={updatingItemId === item.cart_id}
                      >
                        {updatingItemId === item.cart_id ? '...' : '+'}
                      </button>
                    </div>
                    
                    <button 
                      className="remove-btn"
                      onClick={() => removeFromCartHandler(item.cart_id)}
                      disabled={updatingItemId === item.cart_id}
                    >
                      {updatingItemId === item.cart_id ? 'Updating...' : 'Remove'}
                    </button>
                  </div>

                  <div className="item-price">
                    <div className="price-total">
                      ${(parseFloat(item.price) * item.quantity).toFixed(2)}
                    </div>
                    <div className="price-unit">${parseFloat(item.price).toFixed(2)} each</div>
                    <div className="item-total">
                      {item.quantity} √ó ${parseFloat(item.price).toFixed(2)}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="cart-summary">
            <h3>Order Summary</h3>
            
            <div className="summary-row">
              <span>Subtotal ({calculateTotalItems()} items):</span>
              <span>${calculateSubtotal()}</span>
            </div>
            
            <div className="summary-row">
              <span>Shipping:</span>
              <span style={{ color: '#27ae60', fontWeight: '600' }}>FREE</span>
            </div>
            
            <div className="summary-row">
              <span>Tax (10%):</span>
              <span>${calculateTax()}</span>
            </div>
            
            <div className="summary-divider"></div>
            
            <div className="summary-row total">
              <span>Total:</span>
              <span>${calculateTotal()}</span>
            </div>

            {isQuickCheckout ? (
              <>
                <button 
                  className="checkout-btn quick-checkout"
                  onClick={handleCheckout}
                  disabled={checkoutProcessing}
                >
                  {checkoutProcessing ? (
                    <>
                      <span className="spinner"></span> Processing...
                    </>
                  ) : (
                    '‚ö° Complete Quick Checkout'
                  )}
                </button>
                <p style={{
                  fontSize: '0.9rem',
                  color: '#7f8c8d',
                  textAlign: 'center',
                  marginTop: '10px',
                  marginBottom: '15px'
                }}>
                  Quick checkout with your selected item
                </p>
              </>
            ) : (
              <button 
                className="checkout-btn"
                onClick={handleCheckout}
                disabled={checkoutProcessing}
              >
                {checkoutProcessing ? (
                  <>
                    <span className="spinner"></span> Processing...
                  </>
                ) : (
                  'Proceed to Checkout'
                )}
              </button>
            )}

            <button 
              className="continue-shopping-btn"
              onClick={() => window.location.href = '/library'}
              disabled={checkoutProcessing}
            >
              Continue Shopping
            </button>
            
            <button 
              className="continue-shopping-btn"
              onClick={() => window.location.href = '/home'}
              style={{ marginTop: '10px', borderColor: '#667eea', color: '#667eea' }}
              disabled={checkoutProcessing}
            >
              Back to Home
            </button>

            <div className="payment-methods">
              <p style={{ fontSize: '0.9rem', color: '#7f8c8d', textAlign: 'center', marginTop: '20px' }}>
                Secure payment ‚Ä¢ 100% satisfaction guarantee ‚Ä¢ Easy returns
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Add spinner CSS */}
      <style jsx>{`
        .spinner {
          display: inline-block;
          width: 16px;
          height: 16px;
          border: 2px solid rgba(255,255,255,.3);
          border-radius: 50%;
          border-top-color: #fff;
          animation: spin 1s ease-in-out infinite;
          margin-right: 8px;
        }
        
        .updating-spinner {
          display: inline-block;
          width: 12px;
          height: 12px;
          border: 2px solid rgba(52, 152, 219, 0.3);
          border-radius: 50%;
          border-top-color: #3498db;
          animation: spin 1s ease-in-out infinite;
        }
        
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
        
        .cart-items-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 20px;
          padding-bottom: 10px;
          border-bottom: 2px solid #ecf0f1;
        }
        
        .cart-summary-text {
          color: #7f8c8d;
          margin-top: 5px;
          font-size: 1.1rem;
        }
        
        .item-total {
          color: #7f8c8d;
          font-size: 0.8rem;
          margin-top: 5px;
        }
        
        .item-pages {
          color: #7f8c8d;
          font-size: 0.9rem;
          margin-top: 5px;
        }
      `}</style>
    </>
  );
};

export default Cart;
