import React, { useState, useEffect } from 'react';
import '../Styles/Cart.css';
import CartItem from '../Components/CartItem';
import { 
  getCartItems,
  updateCartQuantity,
  removeFromCart,
  clearCart,
  checkout 
} from "../services/api";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch cart items
  const fetchCartData = async () => {
    try {
      console.log('üõí Fetching cart items...');
      setLoading(true);
      const response = await getCartItems();
      console.log('‚úÖ Cart items:', response.data);
      
      setCartItems(response.data);
      setError(null);
    } catch (err) {
      console.error('‚ùå Error fetching cart:', err);
      setError('Failed to load cart. Please check backend.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCartData();
  }, []);

  // Update quantity
  const handleUpdateQuantity = async (cartItemId, newQuantity) => {
    try {
      const response = await updateCartQuantity(cartItemId, newQuantity);
      
      if (response.data.success) {
        setCartItems(prevItems => 
          prevItems.map(item => 
            item.id == cartItemId ? { ...item, quantity: newQuantity } : item
          )
        );
        setSuccessMessage('Quantity updated!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 3000);
      }
    } catch (err) {
      console.error('Error updating quantity:', err);
      setError(err.response?.data?.error || 'Update failed');
      fetchCartData(); // Refresh on error
    }
  };

  // Remove item
  const handleRemoveItem = async (cartItemId) => {
    try {
      const response = await removeFromCart(cartItemId);
      
      if (response.data.success) {
        setCartItems(prevItems => 
          prevItems.filter(item => item.id != cartItemId)
        );
        setSuccessMessage('Item removed!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 3000);
      }
    } catch (err) {
      console.error('Error removing item:', err);
      setError(err.response?.data?.error || 'Remove failed');
    }
  };

  // Clear cart
  const handleClearCart = async () => {
    try {
      await clearCart();
      setCartItems([]);
      setSuccessMessage('Cart cleared!');
      setError(null);
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      console.error('Error clearing cart:', err);
      setError('Failed to clear cart');
    }
  };

  // Checkout
  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      setError('Cart is empty');
      return;
    }

    try {
      const response = await checkout();
      
      if (response.data.success) {
        // Show order summary
        const orderSummary = response.data.orderSummary || [];
        const summaryText = orderSummary.map(item => 
          `‚Ä¢ ${item.title} (x${item.quantity}): $${item.subtotal}`
        ).join('\n');
        
        alert(`‚úÖ ORDER PLACED!\n\nOrder ID: ${response.data.orderId}\nItems: ${response.data.itemsPurchased}\nTotal: $${response.data.total}\n\nOrder Summary:\n${summaryText}\n\nThank you for your purchase!`);
        
        setCartItems([]);
        setSuccessMessage('Order placed successfully!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 5000);
      }
    } catch (err) {
      console.error('Checkout error:', err);
      
      if (err.response?.data?.outOfStock) {
        const outOfStockList = err.response.data.outOfStock.map(item => 
          `‚Ä¢ ${item.title}: Only ${item.available} available (requested ${item.requested})`
        ).join('\n');
        
        alert(`‚ùå CHECKOUT FAILED!\n\nSome items are out of stock:\n${outOfStockList}\n\nPlease update your cart and try again.`);
        setError('Some items are out of stock. Please update your cart.');
      } else {
        alert(`‚ùå Checkout failed: ${err.response?.data?.error || err.message}`);
        setError(err.response?.data?.error || 'Checkout failed');
      }
      
      // Refresh cart data
      fetchCartData();
    }
  };

  // Calculate order summary
  const calculateOrderSummary = () => {
    const subtotal = cartItems.reduce((sum, item) => {
      const price = parseFloat(item.price) || 0;
      const quantity = parseInt(item.quantity) || 1;
      return sum + (price * quantity);
    }, 0);
    
    const tax = subtotal * 0.10;
    const shipping = subtotal > 0 ? 0 : 0;
    const total = subtotal + tax + shipping;
    
    return {
      subtotal: subtotal.toFixed(2),
      tax: tax.toFixed(2),
      shipping: shipping.toFixed(2),
      total: total.toFixed(2),
      shippingText: 'FREE'
    };
  };

  const orderSummary = calculateOrderSummary();

  if (loading) {
    return (
      <div className="cart-container">
        <h1>OnlineBookStore - Shopping Cart</h1>
        <div className="loading">
          <div className="spinner"></div>
          Loading cart...
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>OnlineBookStore - Shopping Cart</h1>
      
      {/* Messages */}
      {successMessage && (
        <div className="success-message">
          ‚úÖ {successMessage}
        </div>
      )}
      
      {error && (
        <div className="error-message">
          ‚ùå {error}
          <button onClick={fetchCartData} style={{marginLeft: '10px', padding: '5px 10px'}}>
            Refresh
          </button>
        </div>
      )}
      
      <div className="cart-content">
        <div className="cart-items-section">
          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px'}}>
            <h2>
              Your Cart ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'})
            </h2>
            {cartItems.length > 0 && (
              <button onClick={handleClearCart} className="clear-cart-btn">
                Clear All
              </button>
            )}
          </div>
          
          {cartItems.length === 0 ? (
            <div className="empty-cart">
              <p style={{fontSize: '1.2rem', marginBottom: '20px', color: '#666'}}>
                Your shopping cart is empty
              </p>
              <a href="/home" className="continue-shopping-btn">
                ‚Üê Continue Shopping
              </a>
            </div>
          ) : (
            <div className="cart-items-list">
              {cartItems.map((item) => (
                <CartItem
                  key={item.id}
                  item={item}
                  onUpdateQuantity={handleUpdateQuantity}
                  onRemoveItem={handleRemoveItem}
                />
              ))}
            </div>
          )}
        </div>
        
        {cartItems.length > 0 && (
          <div className="order-summary">
            <h3>Order Summary</h3>
            <div className="summary-details">
              <div className="summary-row">
                <span>Subtotal ({cartItems.length} items):</span>
                <span style={{fontWeight: '600'}}>${orderSummary.subtotal}</span>
              </div>
              <div className="summary-row">
                <span>Shipping:</span>
                <span style={{color: '#28a745', fontWeight: '600'}}>{orderSummary.shippingText}</span>
              </div>
              <div className="summary-row">
                <span>Tax (10%):</span>
                <span>${orderSummary.tax}</span>
              </div>
              <div className="summary-row total">
                <span>Total Amount:</span>
                <span style={{fontSize: '1.4rem', fontWeight: '700', color: '#2d3748'}}>
                  ${orderSummary.total}
                </span>
              </div>
            </div>
            
            <button className="checkout-btn" onClick={handleCheckout}>
              Proceed to Checkout
            </button>
            
            <div className="cart-actions">
              <a href="/home" className="continue-link">‚Üê Continue Shopping</a>
              <a href="/library" className="back-link">Browse More Books ‚Üí</a>
            </div>
            
            <div className="guarantee">
              <p style={{fontSize: '0.9rem', color: '#666', lineHeight: '1.5'}}>
                üîí Secure payment ‚Ä¢ ‚úÖ 100% satisfaction guarantee ‚Ä¢ ‚Ü©Ô∏è Easy returns within 30 days ‚Ä¢ üöö Free shipping
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
