import React, { useState, useEffect, useMemo } from "react";
import Navbar from "../Components/NavBar";
import '../Styles/Home.css';
import { 
  getBooks,
  addToCart as addToCartAPI,
  getCartItems
} from "../services/api";

export default function HomePage() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cartCount, setCartCount] = useState(0);

  // Fetch books from database
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);
        const response = await getBooks();
        setBooks(response.data);
      } catch (error) {
        console.error('Error fetching books:', error);
        alert('Failed to load books from database');
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  // Fetch cart count from database
  useEffect(() => {
    const fetchCartCount = async () => {
      try {
        const response = await getCartItems();
        setCartCount(response.data.length);
      } catch (error) {
        console.error('Error fetching cart count:', error);
      }
    };

    fetchCartCount();
  }, []);

  // Add to cart function - DATABASE DRIVEN
  const addToCart = async (book) => {
    try {
      console.log('Adding to cart:', book);
      
      // Call API to add to database
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.action === 'added' || response.data.action === 'updated') {
        // Update cart count
        const cartResponse = await getCartItems();
        setCartCount(cartResponse.data.length);
        
        // Show success message
        alert(`‚úÖ Added "${book.title}" to cart!`);
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('‚ùå Error adding book to cart. Please try again.');
    }
  };

  // Quick checkout function - DATABASE DRIVEN
  const quickCheckout = async (book) => {
    try {
      console.log('Quick checkout for:', book);
      
      // Clear existing cart first using API
      const clearResponse = await fetch(`http://localhost:5000/api/cart/clear/1`, {
        method: 'DELETE'
      });
      
      if (!clearResponse.ok) {
        throw new Error('Failed to clear cart');
      }
      
      // Add the single book to cart
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.action === 'added') {
        // Set quick checkout flag
        localStorage.setItem('isQuickCheckout', 'true');
        
        // Update cart count
        setCartCount(1);
        
        // Navigate to cart page
        window.location.href = '/cart';
      }
    } catch (error) {
      console.error('Error in quick checkout:', error);
      alert('‚ùå Error during quick checkout. Please try again.');
    }
  };

  // Find the 4 most popular categories (genres with most books) from database
  const popularCategories = useMemo(() => {
    if (books.length === 0) return [];
    
    const genreCounts = {};
    
    books.forEach(book => {
      genreCounts[book.genre] = (genreCounts[book.genre] || 0) + 1;
    });

    // Sort genres by count and take top 4
    return Object.entries(genreCounts)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 4)
      .map(([genre]) => genre);
  }, [books]);

  // Get 5 books for each popular category from database
  const categoryBooks = useMemo(() => {
    const result = {};
    
    popularCategories.forEach(genre => {
      const booksInGenre = books
        .filter(book => book.genre === genre)
        .slice(0, 5); // Take first 5 books
      
      result[genre] = booksInGenre;
    });
    
    return result;
  }, [popularCategories, books]);

  if (loading && books.length === 0) {
    return (
      <>
        <Navbar cartCount={cartCount} />
        <div className="home-container">
          <div className="hero-section">
            <div className="hero-content">
              <h1>Welcome to the Online Book Store</h1>
              <p>Loading books from database...</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar cartCount={cartCount} />
      <div className="home-container">
        {/* Hero Section */}
        <div className="hero-section">
          <div className="hero-content">
            <h1>Welcome to the Online Book Store</h1>
            <p>Discover your next favorite book from our collection of {books.length} books</p>
          </div>
        </div>

        {/* Popular Categories Section */}
        <div className="categories-section">
          <div className="container">
            <h2 className="section-title">Popular Categories</h2>
            
            {popularCategories.length === 0 ? (
              <div style={{ textAlign: 'center', padding: '40px' }}>
                <p>No categories found. Loading books...</p>
              </div>
            ) : (
              popularCategories.map(category => (
                <div key={category} className="category-row">
                  <h3 className="category-title">{category}</h3>
                  <div className="books-row">
                    {categoryBooks[category]?.map(book => (
                      <div 
                        key={book.id} 
                        className="book-card"
                      >
                        <div className="book-image-container">
                          <img 
                            src={book.image_url || `https://picsum.photos/300/400?random=${book.id}`} 
                            alt={book.title} 
                            className="book-image"
                            onError={(e) => {
                              e.target.src = `https://picsum.photos/300/400?random=${book.id}`;
                            }}
                          />
                          <div className="book-overlay">
                            <button 
                              className="add-to-cart-btn"
                              onClick={() => addToCart(book)}
                            >
                              üõí Add to Cart
                            </button>
                            <button 
                              className="quick-checkout-btn"
                              onClick={() => quickCheckout(book)}
                            >
                              ‚ö° Quick Checkout
                            </button>
                          </div>
                        </div>
                        
                        <div className="book-info">
                          <h4 className="book-title">{book.title}</h4>
                          <p className="book-author">by {book.author}</p>
                          <div className="book-meta">
                            <span className="book-rating">‚≠ê {book.rating || '4.0'}</span>
                          </div>
                          <p className="book-price">${parseFloat(book.price).toFixed(2)}</p>
                        </div>

                        {/* Hover Description */}
                        <div className="book-description">
                          <h4>About this book:</h4>
                          <p>{book.description || 'A captivating book that will keep you engaged from start to finish.'}</p>
                          <div className="description-actions">
                            <button 
                              className="description-add-cart"
                              onClick={() => addToCart(book)}
                            >
                              Add to Cart
                            </button>
                            <button 
                              className="description-checkout"
                              onClick={() => quickCheckout(book)}
                            >
                              Quick Checkout
                            </button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Call to Action */}
        <div className="cta-section">
          <div className="container">
            <h2>Ready to Explore More?</h2>
            <p>Discover our complete collection with {books.length} books across all genres</p>
            <button 
              className="browse-all-btn"
              onClick={() => window.location.href = '/library'}
            >
              Browse Full Library
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
