// Cart.js - Complete Fixed Version
import React, { useState, useEffect } from 'react';
import '../Styles/Cart.css';
import CartItem from '../Components/CartItem';
import { 
  getCartItems,
  updateCartQuantity,
  removeFromCart,
  clearCart 
} from "../services/api";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch cart items
  const fetchCartData = async () => {
    try {
      console.log('üõí Fetching cart items...');
      setLoading(true);
      const response = await getCartItems();
      console.log('‚úÖ Cart API response:', response.data);
      
      // Debug: Log the raw data structure
      console.log('üìä Raw cart data structure:');
      response.data.forEach((item, index) => {
        console.log(`üì¶ Item ${index + 1}:`, {
          id: item.id,
          cart_item_id: item.cart_item_id,
          cartId: item.cartId,
          cart_id: item.cart_id,
          book_id: item.book_id,
          title: item.title,
          quantity: item.quantity
        });
      });
      
      // Format items with all possible ID fields
      const formattedItems = response.data.map(item => {
        // Determine the cart item ID - check all possible fields
        const cartItemId = item.id || item.cart_item_id || item.cartId || item.cart_id;
        
        console.log(`üîÑ Formatting item "${item.title}":`, {
          rawId: item.id,
          cart_item_id: item.cart_item_id,
          cartId: item.cartId,
          cart_id: item.cart_id,
          determinedId: cartItemId
        });
        
        return {
          ...item,
          id: cartItemId, // Set as main ID
          cartId: cartItemId, // Also as cartId
          cart_item_id: cartItemId, // Ensure consistency
          bookId: item.book_id || item.bookId,
          price: parseFloat(item.price) || 0,
          quantity: parseInt(item.quantity) || 1
        };
      });
      
      console.log('üéØ Final formatted items:', formattedItems);
      setCartItems(formattedItems);
      setError(null);
    } catch (err) {
      console.error('‚ùå Error fetching cart items:', err);
      console.error('Error details:', err.response?.data || err.message);
      setError(`Failed to load cart items: ${err.message}. Please check if backend is running.`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCartData();
  }, []);

  // Update quantity function
  const handleUpdateQuantity = async (cartItemId, newQuantity) => {
    console.log('üîÑ handleUpdateQuantity called:', { cartItemId, newQuantity });
    
    if (!cartItemId || cartItemId === 'undefined') {
      console.error('‚ùå Invalid cartItemId:', cartItemId);
      setError(`Cannot update: Invalid cart item ID (${cartItemId})`);
      return;
    }

    try {
      console.log(`üì§ Sending update to backend: cartItemId=${cartItemId}, quantity=${newQuantity}`);
      const response = await updateCartQuantity(cartItemId, newQuantity);
      
      console.log('‚úÖ Update response:', response.data);
      
      if (response.data.success) {
        // Update local state
        setCartItems(prevItems => 
          prevItems.map(item => {
            const itemId = item.id || item.cartId || item.cart_item_id;
            console.log(`üîÑ Comparing: itemId=${itemId}, cartItemId=${cartItemId}, match=${itemId == cartItemId}`);
            return (itemId == cartItemId) ? { ...item, quantity: newQuantity } : item;
          })
        );
        setSuccessMessage(`Quantity updated to ${newQuantity}!`);
        setError(null);
        
        // Refresh cart data to ensure consistency
        setTimeout(() => fetchCartData(), 500);
        
        // Clear success message after 3 seconds
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        console.error('‚ùå Update failed:', response.data.error);
        setError(`Failed: ${response.data.error || 'Unknown error'}`);
      }
    } catch (err) {
      console.error('‚ùå Error updating quantity:', err);
      console.error('Error response:', err.response?.data);
      setError(`Update failed: ${err.response?.data?.error || err.message || 'Network error'}`);
      
      // Refresh cart data on error
      fetchCartData();
    }
  };

  // Remove item function
  const handleRemoveItem = async (cartItemId) => {
    console.log('üóëÔ∏è handleRemoveItem called:', cartItemId);
    
    if (!cartItemId || cartItemId === 'undefined') {
      console.error('‚ùå Invalid cartItemId:', cartItemId);
      setError(`Cannot remove: Invalid cart item ID (${cartItemId})`);
      return;
    }

    try {
      const response = await removeFromCart(cartItemId);
      console.log('‚úÖ Remove response:', response.data);
      
      if (response.data.success) {
        // Remove from local state
        setCartItems(prevItems => 
          prevItems.filter(item => {
            const itemId = item.id || item.cartId || item.cart_item_id;
            return itemId != cartItemId;
          })
        );
        setSuccessMessage('Item removed!');
        setError(null);
        
        // Clear success message after 3 seconds
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setError('Failed to remove item');
      }
    } catch (err) {
      console.error('‚ùå Error removing item:', err);
      setError(`Remove failed: ${err.response?.data?.error || err.message}`);
      
      // Refresh cart data on error
      fetchCartData();
    }
  };

  // Clear cart function
  const handleClearCart = async () => {
    try {
      const response = await clearCart();
      if (response.data.success) {
        setCartItems([]);
        setSuccessMessage('Cart cleared!');
        setError(null);
        setTimeout(() => setSuccessMessage(''), 3000);
      }
    } catch (err) {
      console.error('Error clearing cart:', err);
      setError('Failed to clear cart');
    }
  };

  // Calculate order summary
  const calculateOrderSummary = () => {
    const subtotal = cartItems.reduce((sum, item) => {
      const price = parseFloat(item.price) || 0;
      const quantity = parseInt(item.quantity) || 1;
      return sum + (price * quantity);
    }, 0);
    
    const tax = subtotal * 0.10;
    const shipping = subtotal > 0 ? 0 : 0;
    const total = subtotal + tax + shipping;
    
    return {
      subtotal: subtotal.toFixed(2),
      tax: tax.toFixed(2),
      shipping: shipping.toFixed(2),
      total: total.toFixed(2),
      shippingText: subtotal > 0 ? 'FREE' : '$0.00'
    };
  };

  const orderSummary = calculateOrderSummary();

  if (loading) {
    return (
      <div className="cart-container">
        <h1>OnlineBookStore - Shopping Cart</h1>
        <div className="loading">
          <div className="spinner"></div>
          Loading cart from database...
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>OnlineBookStore - Shopping Cart</h1>
      
      {/* Debug Controls (Remove in production) */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{
          background: 'rgba(0,0,0,0.1)',
          padding: '10px',
          borderRadius: '8px',
          marginBottom: '20px',
          color: 'white',
          fontSize: '0.9rem'
        }}>
          <strong>Debug Mode:</strong> 
          <button 
            onClick={fetchCartData}
            style={{
              marginLeft: '10px',
              background: '#4f46e5',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Refresh Cart Data
          </button>
          <button 
            onClick={() => console.log('Cart Items:', cartItems)}
            style={{
              marginLeft: '10px',
              background: '#059669',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Log Cart Items
          </button>
        </div>
      )}
      
      {/* Messages */}
      {successMessage && (
        <div className="success-message">
          ‚úÖ {successMessage}
        </div>
      )}
      
      {error && (
        <div className="error-message">
          <div>
            ‚ùå {error}
          </div>
          <div style={{ marginTop: '10px' }}>
            <button 
              onClick={fetchCartData}
              style={{
                background: '#4f46e5',
                color: 'white',
                border: 'none',
                padding: '8px 15px',
                borderRadius: '4px',
                cursor: 'pointer',
                marginRight: '10px'
              }}
            >
              Refresh Cart
            </button>
            <button 
              onClick={() => window.location.reload()}
              style={{
                background: '#dc3545',
                color: 'white',
                border: 'none',
                padding: '8px 15px',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Reload Page
            </button>
          </div>
        </div>
      )}
      
      <div className="cart-content">
        <div className="cart-items-section">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <h2>
              Your Cart ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'})
              {cartItems.length > 0 && (
                <span style={{ fontSize: '0.9rem', color: '#666', marginLeft: '10px', fontWeight: 'normal' }}>
                  (Total: ${orderSummary.total})
                </span>
              )}
            </h2>
            {cartItems.length > 0 && (
              <button 
                onClick={handleClearCart}
                className="clear-cart-btn"
              >
                Clear All Items
              </button>
            )}
          </div>
          
          {cartItems.length === 0 ? (
            <div className="empty-cart">
              <p style={{ fontSize: '1.2rem', marginBottom: '20px', color: '#666' }}>
                Your shopping cart is empty
              </p>
              <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
                <a href="/home" className="continue-shopping-btn">
                  ‚Üê Back to Home
                </a>
                <a href="/library" className="continue-shopping-btn" style={{ background: '#059669' }}>
                  Browse Library ‚Üí
                </a>
              </div>
            </div>
          ) : (
            <>
              <div className="cart-items-list">
                {cartItems.map((item, index) => {
                  const itemId = item.id || item.cartId || item.cart_item_id;
                  return (
                    <CartItem
                      key={`${itemId}-${index}`}
                      item={item}
                      onUpdateQuantity={handleUpdateQuantity}
                      onRemoveItem={handleRemoveItem}
                    />
                  );
                })}
              </div>
              
              {/* Debug info for developers */}
              {process.env.NODE_ENV === 'development' && (
                <div style={{
                  marginTop: '20px',
                  padding: '15px',
                  background: '#f8f9fa',
                  borderRadius: '8px',
                  fontSize: '0.8rem',
                  color: '#666',
                  border: '1px dashed #ccc'
                }}>
                  <strong>Cart Debug Info:</strong>
                  <div style={{ marginTop: '5px' }}>
                    Items: {cartItems.length} | 
                    First Item ID: {cartItems[0]?.id} | 
                    Backend: <a href="http://localhost:5000/api/cart-debug" target="_blank" rel="noreferrer" style={{ color: '#4f46e5' }}>
                      Check Backend
                    </a>
                  </div>
                </div>
              )}
            </>
          )}
        </div>
        
        {cartItems.length > 0 && (
          <div className="order-summary">
            <h3>Order Summary</h3>
            <div className="summary-details">
              <div className="summary-row">
                <span>Subtotal ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'}):</span>
                <span style={{ fontWeight: '600' }}>${orderSummary.subtotal}</span>
              </div>
              <div className="summary-row">
                <span>Shipping:</span>
                <span style={{ color: '#28a745', fontWeight: '600' }}>{orderSummary.shippingText}</span>
              </div>
              <div className="summary-row">
                <span>Tax (10%):</span>
                <span>${orderSummary.tax}</span>
              </div>
              <div className="summary-row total">
                <span>Total Amount:</span>
                <span style={{ fontSize: '1.4rem', fontWeight: '700', color: '#2d3748' }}>
                  ${orderSummary.total}
                </span>
              </div>
            </div>
            
            <button 
              className="checkout-btn"
              onClick={() => {
                alert(`‚úÖ Order placed successfully!\nTotal: $${orderSummary.total}\nThank you for your purchase!`);
                handleClearCart();
              }}
            >
              Proceed to Checkout
            </button>
            
            <div className="cart-actions">
              <a href="/home" className="continue-link">‚Üê Continue Shopping</a>
              <a href="/library" className="back-link">Browse More Books ‚Üí</a>
            </div>
            
            <div className="guarantee">
              <p style={{ fontSize: '0.9rem', color: '#666', lineHeight: '1.5' }}>
                üîí Secure payment ‚Ä¢ ‚úÖ 100% satisfaction guarantee ‚Ä¢ ‚Ü©Ô∏è Easy returns within 30 days ‚Ä¢ üöö Free shipping on all orders
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
