import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000,
});

// Get current user ID
const getCurrentUserId = () => {
  return 1;
};

// ========== BOOK APIs ==========
export const getBooks = () => API.get('/books');
export const searchBooks = (term) => API.get(`/books/search?term=${encodeURIComponent(term)}`);
export const filterBooksByGenre = (genre) => API.get(`/books/filter/${encodeURIComponent(genre)}`);

// ========== CART APIs ==========
export const addToCart = (bookId, quantity = 1) => {
  const userId = getCurrentUserId();
  console.log('Adding to cart:', { userId, bookId, quantity });
  return API.post('/cart/add', { userId, bookId, quantity });
};

export const getCartItems = () => {
  return API.get('/cart');
};

export const removeFromCart = (cartItemId) => {
  console.log('Removing cart item:', cartItemId);
  return API.delete(`/cart/${cartItemId}`);
};

export const updateCartQuantity = (cartItemId, quantity) => {
  console.log('Updating cart quantity:', { cartItemId, quantity });
  return API.put(`/cart/${cartItemId}`, { quantity });
};

export const clearCart = () => {
  const userId = getCurrentUserId();
  return API.delete(`/cart/clear/${userId}`);
};

// NEW: Checkout function
export const checkout = () => {
  console.log('Processing checkout...');
  return API.post('/cart/checkout');
};

// ========== HELPER FUNCTIONS ==========
export const getCartItemCount = async () => {
  try {
    const response = await getCartItems();
    return response.data.length;
  } catch (error) {
    console.error('Error getting cart count:', error);
    return 0;
  }
};

// Add error interceptor
API.interceptors.response.use(
  (response) => {
    console.log('✅ API Response:', response.config.url, response.status);
    return response;
  },
  (error) => {
    console.error('❌ API Error:', error.config?.url, error.response?.status, error.message);
    return Promise.reject(error);
  }
);

export default API;
