import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Get current user ID (for now, we'll use a mock user)
// In a real app, this would come from authentication
const getCurrentUserId = () => {
  // For demo, use user ID 1 (the testuser we created)
  // You can modify this based on your login system
  return 1;
};

// Book APIs
export const getBooks = () => API.get('/books');
export const searchBooks = (term) => API.get(`/books/search?term=${term}`);
export const filterBooksByGenre = (genre) => API.get(`/books/filter/${genre}`);

// Cart APIs
export const addToCart = (bookId, quantity = 1) => {
  const userId = getCurrentUserId();
  return API.post('/cart/add', { userId, bookId, quantity });
};

export const getCartItems = () => {
  const userId = getCurrentUserId();
  return API.get(`/cart/${userId}`);
};

export const removeFromCart = (cartId) => {
  return API.delete(`/cart/remove/${cartId}`);
};

export const updateCartQuantity = (cartId, quantity) => {
  return API.put(`/cart/update/${cartId}`, { quantity });
};

export const clearCart = () => {
  const userId = getCurrentUserId();
  return API.delete(`/cart/clear/${userId}`);
};

// Checkout API
export const processCheckout = (cartItems, totalAmount) => {
  const userId = getCurrentUserId();
  return API.post('/checkout', { 
    userId, 
    items: cartItems, 
    totalAmount,
    timestamp: new Date().toISOString()
  });
};

export default API;
