import React, { useState, useEffect } from 'react';
import '../Styles/Library.css';
import Navbar from '../Components/NavBar';
import { 
  getBooks, 
  searchBooks, 
  filterBooksByGenre,
  addToCart as addToCartAPI
} from '../services/api';

const Library = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('title');
  const [filterGenre, setFilterGenre] = useState('all');
  const [hoveredBook, setHoveredBook] = useState(null);
  const [genres, setGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cartCount, setCartCount] = useState(0);

  // Fetch books from database
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);
        const response = await getBooks();
        setBooks(response.data);
        
        // Extract unique genres
        const uniqueGenres = [...new Set(response.data.map(book => book.genre))];
        setGenres(['all', ...uniqueGenres]);
      } catch (error) {
        console.error('Error fetching books:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  // Add to cart function - UPDATED with database
  const addToCart = async (book) => {
    try {
      console.log('Adding to cart:', book);
      
      // Call API to add to database
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.action === 'added' || response.data.action === 'updated') {
        // Update local cart count
        setCartCount(prev => prev + 1);
        
        // Show success message
        alert(`âœ… Added "${book.title}" to cart!`);
        
        // Update cart badge in navbar (if you have one)
        updateNavbarCartCount();
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('âŒ Error adding book to cart. Please try again.');
    }
  };

  // Quick checkout function - UPDATED
  const quickCheckout = async (book) => {
    try {
      console.log('Quick checkout for:', book);
      
      // Clear existing cart first
      const userId = 1; // Mock user ID
      const clearResponse = await fetch(`http://localhost:5000/api/cart/clear/${userId}`, {
        method: 'DELETE'
      });
      
      if (!clearResponse.ok) {
        console.warn('Could not clear cart, but continuing...');
      }
      
      // Add the single book to cart
      const response = await addToCartAPI(book.id, 1);
      
      if (response.data.action === 'added') {
        // Set quick checkout flag in localStorage
        localStorage.setItem('isQuickCheckout', 'true');
        
        // Navigate to cart page
        window.location.href = '/cart';
      }
    } catch (error) {
      console.error('Error in quick checkout:', error);
      alert('âŒ Error during quick checkout. Please try again.');
    }
  };

  // Helper function to update navbar cart count
  const updateNavbarCartCount = async () => {
    try {
      const userId = 1; // Mock user ID
      const response = await fetch(`http://localhost:5000/api/cart/${userId}`);
      if (response.ok) {
        const cartItems = await response.json();
        setCartCount(cartItems.length);
      }
    } catch (error) {
      console.error('Error updating cart count:', error);
    }
  };

  // Load cart count on component mount
  useEffect(() => {
    updateNavbarCartCount();
  }, []);

  // ... rest of your existing code (search, filter, sort functions) ...

  return (
    <>
      <Navbar cartCount={cartCount} />
      <div className="library-container">
        <div className="animated-background"></div>
        
        {/* Floating Cart Button with count */}
        <div className="floating-cart-btn">
          <button 
            className="cart-icon-btn"
            onClick={() => window.location.href = '/cart'}
          >
            ðŸ›’
            {cartCount > 0 && (
              <span className="cart-badge">{cartCount}</span>
            )}
          </button>
        </div>

        {/* ... rest of your existing JSX ... */}
        
        <div className="books-grid">
          {filteredAndSortedBooks.map(book => (
            <div 
              key={book.id} 
              className="book-card"
              onMouseEnter={() => setHoveredBook(book)}
              onMouseLeave={() => setHoveredBook(null)}
            >
              <div className="book-image-container">
                <img src={book.image_url} alt={book.title} className="book-image" />
                <div className="book-overlay">
                  <button 
                    className="add-to-cart-btn"
                    onClick={() => addToCart(book)}
                  >
                    ðŸ›’ Add to Cart
                  </button>
                  <button 
                    className="quick-checkout-btn"
                    onClick={() => quickCheckout(book)}
                  >
                    âš¡ Quick Checkout
                  </button>
                </div>
              </div>
              
              {/* ... rest of book card JSX ... */}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Library;
